import React, { useState, useEffect } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { issueService, IssueFilters } from '../../services/issueService'
import { portfolioService } from '../../services/portfolioService'
import { adminService, PortfolioLock } from '../../services/adminService'
import { Issue, CreateIssueData } from '../../types/issue.types'
import { Portfolio } from '../../types/portfolio.types'
import { User } from '../../types/user.types'
import { useAuth } from '../../context/AuthContext'
import toast from 'react-hot-toast'
import Button from '../common/Button'

const IssueDetailsTable: React.FC = () => {
  const { user } = useAuth()
  const queryClient = useQueryClient()

  // Search and filter states
  const [searchQuery, setSearchQuery] = useState('')
  const [dateFilter, setDateFilter] = useState('')
  const [hourFilter, setHourFilter] = useState<string>(new Date().getHours().toString())
  const [issueFilter, setIssueFilter] = useState<string>('active')
  const [selectedPortfolio, setSelectedPortfolio] = useState<string>('all')
  const [startDate, setStartDate] = useState('')
  const [endDate, setEndDate] = useState('')
  const [quickRange, setQuickRange] = useState<string>('today')

  // Export states
  const [fromDate, setFromDate] = useState('')
  const [toDate, setToDate] = useState('')

  // New issue form states
  const [newIssue, setNewIssue] = useState<Partial<CreateIssueData>>({
    portfolio_id: '',
    issue_hour: new Date().getHours(),
    description: '',
    monitored_by: user?.email ? [user.email] : [],
  })
  const [issuePresent, setIssuePresent] = useState<string>('')
  const [caseNumber, setCaseNumber] = useState('')
  const [missedAlertsBy, setMissedAlertsBy] = useState<string>('')
  const [selectedMonitoredBy, setSelectedMonitoredBy] = useState<string>(user?.email || '')
  const [activeLock, setActiveLock] = useState<PortfolioLock | null>(null)

  // Fetch portfolios
  const { data: portfolios = [] } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  // Fetch users for dropdowns
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['admin-users'],
    queryFn: adminService.getUsers,
  })

  // Fetch locks to detect the current user's active lock
  const { data: locks = [] } = useQuery<PortfolioLock[]>({
    queryKey: ['portfolio-locks'],
    queryFn: adminService.getLocks,
    refetchInterval: 30000,
  })

  // Derive active lock for the logged-in user (current hour)
  useEffect(() => {
    if (!user?.email || locks.length === 0) {
      setActiveLock(null)
      return
    }
    const currentHour = new Date().getHours()
    const lock = locks.find(
      (l) =>
        l.monitored_by?.toLowerCase() === user.email.toLowerCase() &&
        l.issue_hour === currentHour
    )
    setActiveLock(lock || null)
  }, [locks, user?.email])

  // Set default monitored by to current user's email
  useEffect(() => {
    if (user?.email) {
      setSelectedMonitoredBy(user.email)
      setNewIssue(prev => ({
        ...prev,
        monitored_by: [user.email],
      }))
    }
  }, [user])

  // When there is an active lock, prefill and lock portfolio/hour/monitored_by
  useEffect(() => {
    if (activeLock) {
      setNewIssue((prev) => ({
        ...prev,
        portfolio_id: activeLock.portfolio_id,
        issue_hour: activeLock.issue_hour,
        monitored_by: user?.email ? [user.email] : prev.monitored_by,
      }))
      setSelectedMonitoredBy(user?.email || '')
    }
  }, [activeLock, user?.email])


  // Build filters for API
  const buildFilters = (): IssueFilters => {
    const filters: IssueFilters = {}
    if (searchQuery) filters.search = searchQuery
    if (dateFilter) {
      // Add date filtering logic here when backend supports it
    }
    if (hourFilter !== 'all') {
      filters.issue_hour = parseInt(hourFilter)
    }
    // "Active issues" = issues where Issue Present = Yes (description !== 'No issue')
    // "All issues" = all issues (both Yes and No)
    if (issueFilter === 'active') {
      filters.description = '!No issue' // Filter out "No issue" entries
    }
    return filters
  }

  // Fetch issues
  const { data: issues = [], isLoading } = useQuery<Issue[]>({
    queryKey: ['issues', buildFilters()],
    queryFn: () => issueService.getAll(buildFilters()),
  })

  // Create issue mutation
  const createMutation = useMutation({
    mutationFn: issueService.create,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['issues'] })
      queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Issue logged successfully')
      // Reset form
      setNewIssue({
        portfolio_id: '',
        issue_hour: new Date().getHours(),
        description: '',
        monitored_by: user?.email ? [user.email] : [],
      })
      setIssuePresent('')
      setCaseNumber('')
      setMissedAlertsBy('')
      setSelectedMonitoredBy(user?.email || '')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to log issue')
    },
  })

  // Update issue mutation
  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) => issueService.update(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['issues'] })
      queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Issue updated successfully')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to update issue')
    },
  })

  // Delete issue mutation
  const deleteMutation = useMutation({
    mutationFn: issueService.delete,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['issues'] })
      queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Issue deleted successfully')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to delete issue')
    },
  })

  const handleLogTicket = () => {
    // Validation
    if (!newIssue.portfolio_id) {
      toast.error('Please select a Portfolio')
      return
    }

    // Check if portfolio is locked by another user
    if (newIssue.portfolio_id && newIssue.issue_hour !== undefined) {
      const currentHour = newIssue.issue_hour
      const lockForThisPortfolio = locks.find(
        (l) =>
          l.portfolio_id === newIssue.portfolio_id &&
          l.issue_hour === currentHour
      )

      if (lockForThisPortfolio) {
        const isLockedByMe = lockForThisPortfolio.monitored_by?.toLowerCase() === user?.email?.toLowerCase()
        if (!isLockedByMe) {
          toast.error(`This portfolio is locked by ${lockForThisPortfolio.monitored_by?.split('@')[0] || 'another user'}. Only they can log issues.`)
          return
        }
      }
    }

    if (!issuePresent || (issuePresent !== 'yes' && issuePresent !== 'no')) {
      toast.error('Please select "Yes" or "No" for Issue Present')
      return
    }

    if (!selectedMonitoredBy) {
      toast.error('Monitored By is REQUIRED. Please select who monitored this hour.')
      return
    }

    if (issuePresent === 'yes' && (!newIssue.description || newIssue.description.trim() === '')) {
      toast.error('Please provide issue details when issue is present')
      return
    }

    // Build issue data - map to database schema
    // monitored_by is VARCHAR(255) (string), missed_by is TEXT[] (array)
    const issueData: any = {
      portfolio_id: newIssue.portfolio_id!,
      site_name: '',
      issue_hour: newIssue.issue_hour || new Date().getHours(),
      description: issuePresent === 'no' ? 'No issue' : (newIssue.description || '').trim(),
      severity: (issuePresent === 'yes' ? 'high' : 'low').toLowerCase(), // Database uses lowercase
      status: 'open',
      monitored_by: selectedMonitoredBy || '', // Database expects single string, not array
      missed_by: (issuePresent === 'yes' && missedAlertsBy) ? [missedAlertsBy] : null, // Array for missed_by, null if empty
      notes: caseNumber ? `Case #: ${caseNumber}` : null,
    }

    // Remove null/undefined fields
    Object.keys(issueData).forEach(key => {
      if (issueData[key] === null || issueData[key] === undefined) {
        delete issueData[key]
      }
    })

    console.log('Creating issue with data:', issueData)
    createMutation.mutate(issueData)
  }

  const handleClearFilters = () => {
    setSearchQuery('')
    setDateFilter('')
    setHourFilter('all')
    setIssueFilter('active')
  }

  const handleQuickRange = (range: 'today' | 'yesterday' | 'week' | 'month') => {
    const today = new Date()
    const yesterday = new Date(today)
    yesterday.setDate(yesterday.getDate() - 1)
    const weekStart = new Date(today)
    weekStart.setDate(today.getDate() - today.getDay()) // Start of week (Sunday)
    const monthStart = new Date(today.getFullYear(), today.getMonth(), 1)

    switch (range) {
      case 'today':
        setStartDate(today.toISOString().split('T')[0])
        setEndDate(today.toISOString().split('T')[0])
        break
      case 'yesterday':
        setStartDate(yesterday.toISOString().split('T')[0])
        setEndDate(yesterday.toISOString().split('T')[0])
        break
      case 'week':
        setStartDate(weekStart.toISOString().split('T')[0])
        setEndDate(today.toISOString().split('T')[0])
        break
      case 'month':
        setStartDate(monthStart.toISOString().split('T')[0])
        setEndDate(today.toISOString().split('T')[0])
        break
    }
  }

  const handleExport = async (type: 'all' | 'active') => {
    try {
      const filters: IssueFilters = {}
      if (fromDate) {
        // Add date filtering when backend supports it
      }
      if (toDate) {
        // Add date filtering when backend supports it
      }
      if (type === 'active') {
        filters.status = 'open'
      }

      const data = await issueService.export(filters)
      // Handle export download
      toast.success('Export initiated')
    } catch (error: any) {
      toast.error(error.response?.data?.error || 'Failed to export issues')
    }
  }

  const formatDateTime = (dateString: string) => {
    const date = new Date(dateString)
    const month = String(date.getMonth() + 1).padStart(2, '0')
    const day = String(date.getDate()).padStart(2, '0')
    const year = date.getFullYear()
    const hours = String(date.getHours()).padStart(2, '0')
    const minutes = String(date.getMinutes()).padStart(2, '0')
    return `${month}/${day}/${year}, ${hours}:${minutes}`
  }

  const getCurrentDateTime = () => {
    const now = new Date()
    return formatDateTime(now.toISOString())
  }

  return (
    <div className="space-y-6 w-full">
      {/* Search Issues Section */}
      <div className="bg-green-50 rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
          </svg>
          Search Issues
        </h3>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Search by portfolio, issue details, case number, or monitored by...
            </label>
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search..."
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Select Portfolio</label>
              <select
                value={selectedPortfolio}
                onChange={(e) => setSelectedPortfolio(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="all">All Portfolios</option>
                {portfolios.map((p) => (
                  <option key={p.id} value={p.id}>
                    {p.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Issue Filter</label>
              <select
                value={issueFilter}
                onChange={(e) => setIssueFilter(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="active">Active Issues (Default)</option>
                <option value="all">All Issues</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Hour Filter</label>
              <select
                value={hourFilter}
                onChange={(e) => setHourFilter(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="all">All Hours</option>
                {Array.from({ length: 24 }, (_, i) => (
                  <option key={i} value={i.toString()}>
                    {i}:00
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mt-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">QUICK RANGE</label>
              <div className="flex gap-2">
                <button
                  onClick={() => handleQuickRange('today')}
                  className={`flex-1 px-3 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${
                    quickRange === 'today'
                      ? 'bg-green-500 text-white shadow-md'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300 hover:shadow-md hover:scale-105'
                  }`}
                >
                  Today
                </button>
                <button
                  onClick={() => handleQuickRange('yesterday')}
                  className={`flex-1 px-3 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${
                    quickRange === 'yesterday'
                      ? 'bg-green-500 text-white shadow-md'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300 hover:shadow-md hover:scale-105'
                  }`}
                >
                  Yesterday
                </button>
                <button
                  onClick={() => handleQuickRange('week')}
                  className={`flex-1 px-3 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${
                    quickRange === 'week'
                      ? 'bg-green-500 text-white shadow-md'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300 hover:shadow-md hover:scale-105'
                  }`}
                >
                  This Week
                </button>
                <button
                  onClick={() => handleQuickRange('month')}
                  className={`flex-1 px-3 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${
                    quickRange === 'month'
                      ? 'bg-green-500 text-white shadow-md'
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300 hover:shadow-md hover:scale-105'
                  }`}
                >
                  This Month
                </button>
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Start Date</label>
              <div className="relative">
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 pr-10"
                />
                <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">End Date</label>
              <div className="relative">
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 pr-10"
                />
                <svg className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
            </div>
            <div className="flex items-end">
              <Button
                variant="secondary"
                onClick={handleClearFilters}
                className="w-full"
              >
                Clear Filters
              </Button>
            </div>
          </div>
        </div>
      </div>


      {/* Issues Table */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200 flex items-center justify-between">
          <h3 className="text-lg font-semibold text-gray-900">
            Issue Results ({issues.length})
          </h3>
          <p className="text-sm text-gray-600">Showing newest first</p>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">PORTFOLIO</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">HOUR</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">ISSUE PRESENT</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">ISSUE DESCRIPTION</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">CASE #</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">MONITORED BY</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">DATE/TIME</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">MISSED ALERTS BY</th>
                <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase">ACTIONS</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {/* New Entry Row */}
              <tr className="bg-gray-50">
                <td className="px-4 py-3">
                  <select
                    value={newIssue.portfolio_id || ''}
                    onChange={(e) => setNewIssue({ ...newIssue, portfolio_id: e.target.value })}
                    disabled={!!activeLock}
                    className={`w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      activeLock ? 'bg-gray-100 cursor-not-allowed' : ''
                    }`}
                  >
                    <option value="">{activeLock ? 'Locked to portfolio' : 'Select Portfolio'}</option>
                    {portfolios.map((p) => (
                      <option key={p.id} value={p.id}>
                        {p.name}
                      </option>
                    ))}
                  </select>
                </td>
                <td className="px-4 py-3">
                  <input
                    type="number"
                    min="0"
                    max="23"
                    value={newIssue.issue_hour || ''}
                    onChange={(e) => setNewIssue({ ...newIssue, issue_hour: parseInt(e.target.value) || 0 })}
                    disabled={!!activeLock}
                    className={`w-20 px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      activeLock ? 'bg-gray-100 cursor-not-allowed' : ''
                    }`}
                  />
                </td>
                <td className="px-4 py-3">
                  <div className="flex gap-2">
                    <button
                      type="button"
                      onClick={() => {
                        setIssuePresent('yes')
                        if (newIssue.description === 'No issue') {
                          setNewIssue({ ...newIssue, description: '' })
                        }
                      }}
                      className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                        issuePresent === 'yes'
                          ? 'bg-red-100 text-red-800 border-2 border-red-300'
                          : 'bg-white text-gray-700 border-2 border-gray-300 hover:bg-gray-50'
                      }`}
                    >
                      Yes
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        setIssuePresent('no')
                        setNewIssue({ ...newIssue, description: 'No issue' })
                        setMissedAlertsBy('') // Clear missed alerts when No is selected
                      }}
                      className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                        issuePresent === 'no'
                          ? 'bg-green-100 text-green-800 border-2 border-green-300'
                          : 'bg-white text-gray-700 border-2 border-gray-300 hover:bg-gray-50'
                      }`}
                    >
                      No
                    </button>
                  </div>
                </td>
                <td className="px-4 py-3">
                  <input
                    type="text"
                    value={newIssue.description || ''}
                    onChange={(e) => setNewIssue({ ...newIssue, description: e.target.value })}
                    placeholder={issuePresent === '' ? "Select issue present first" : issuePresent === 'no' ? "No issue" : "Enter issue details"}
                    disabled={issuePresent === 'no'}
                    className={`w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      issuePresent === 'no' ? 'bg-gray-100 cursor-not-allowed' : ''
                    }`}
                  />
                </td>
                <td className="px-4 py-3">
                  <input
                    type="text"
                    value={caseNumber}
                    onChange={(e) => setCaseNumber(e.target.value)}
                    placeholder="Case #"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </td>
                <td className="px-4 py-3">
                  <select
                    value={selectedMonitoredBy}
                    onChange={(e) => {
                      setSelectedMonitoredBy(e.target.value)
                      setNewIssue({ ...newIssue, monitored_by: e.target.value ? [e.target.value] : [] })
                    }}
                    disabled={!!activeLock}
                    className={`w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      activeLock ? 'bg-gray-100 cursor-not-allowed' : ''
                    }`}
                  >
                    <option value="">{activeLock ? 'Locked to you' : 'Select'}</option>
                    {users
                      .filter(u => u.is_active)
                      .map((u) => {
                        const username = u.email.split('@')[0]
                        return (
                          <option key={u.id} value={u.email}>
                            {username} ({u.full_name})
                          </option>
                        )
                      })}
                  </select>
                </td>
                <td className="px-4 py-3 text-sm text-gray-600">
                  {getCurrentDateTime()}
                </td>
                <td className="px-4 py-3">
                  <select
                    value={missedAlertsBy}
                    onChange={(e) => setMissedAlertsBy(e.target.value)}
                    disabled={issuePresent === 'no' || issuePresent === ''}
                    className={`w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                      issuePresent === 'no' || issuePresent === '' ? 'bg-gray-100 cursor-not-allowed' : ''
                    }`}
                  >
                    <option value="">Select</option>
                    {users
                      .filter(u => u.is_active)
                      .map((u) => {
                        const username = u.email.split('@')[0]
                        return (
                          <option key={u.id} value={u.email}>
                            {username} ({u.full_name})
                          </option>
                        )
                      })}
                  </select>
                </td>
                <td className="px-4 py-3">
                  <Button
                    variant="primary"
                    size="sm"
                    onClick={handleLogTicket}
                    disabled={createMutation.isPending}
                    style={{ backgroundColor: '#76ab3f' }}
                  >
                    {createMutation.isPending ? 'Logging...' : 'Log Ticket'}
                  </Button>
                </td>
              </tr>

              {/* Existing Issues Rows */}
              {isLoading ? (
                <tr>
                  <td colSpan={7} className="px-4 py-8 text-center text-gray-500">
                    Loading issues...
                  </td>
                </tr>
              ) : issues.length === 0 ? (
                <tr>
                  <td colSpan={7} className="px-4 py-8 text-center text-gray-500">
                    <div className="space-y-2">
                      <p className="font-medium">No issues found</p>
                      <p className="text-sm text-gray-400">
                        {issueFilter === 'active' 
                          ? "No issues with 'Issue Present = Yes' found. Select 'All Issues' to see all issues, or try selecting a different portfolio or adjusting the date range."
                          : "No issues found. Try selecting a different portfolio or adjusting the date range."}
                      </p>
                    </div>
                  </td>
                </tr>
              ) : (
                issues.map((issue) => (
                  <tr 
                    key={issue.id} 
                    className="hover:bg-blue-50 hover:shadow-sm transition-all duration-200 cursor-pointer border-l-4 border-l-transparent hover:border-l-blue-500"
                  >
                    <td className="px-4 py-3 text-sm text-gray-900">
                      {issue.portfolio?.name || 'N/A'}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-900">{issue.issue_hour}</td>
                    <td className="px-4 py-3">
                      {/* Show badge based on issue description or status */}
                      {issue.description && issue.description.toLowerCase() === 'no issue' ? (
                        <span className="px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                          No
                        </span>
                      ) : issue.description && issue.description.trim() !== '' ? (
                        <span className="px-3 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                          Yes
                        </span>
                      ) : (
                        <span className="px-3 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                          -
                        </span>
                      )}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-900 max-w-xs">
                      {issue.description}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-600">-</td>
                    <td className="px-4 py-3 text-sm text-gray-900">
                      {issue.monitored_by?.map(email => email.split('@')[0]).join(', ') || '-'}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-600">
                      {formatDateTime(issue.created_at)}
                    </td>
                    <td className="px-4 py-3 text-sm text-gray-600">
                      {issue.missed_by?.map(email => email.split('@')[0]).join(', ') || '-'}
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex gap-2">
                        <button
                          onClick={() => {
                            // Handle edit
                            toast('Edit functionality coming soon', { icon: '' })
                          }}
                          className="text-blue-600 hover:text-blue-800 hover:underline hover:font-semibold text-sm transition-all duration-200"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => {
                            if (window.confirm('Are you sure you want to delete this issue?')) {
                              deleteMutation.mutate(issue.id)
                            }
                          }}
                          className="text-red-600 hover:text-red-800 hover:underline hover:font-semibold text-sm transition-all duration-200"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}

export default IssueDetailsTable
