import React, { useState, useMemo } from 'react'
import { useQuery } from '@tanstack/react-query'
import { Bar } from 'react-chartjs-2'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js'
import { issueService } from '../../services/issueService'
import { portfolioService } from '../../services/portfolioService'
import { adminService } from '../../services/adminService'
import { Issue } from '../../types/issue.types'
import { Portfolio } from '../../types/portfolio.types'
import { User } from '../../types/user.types'
import { useAuth } from '../../context/AuthContext'
import Card from '../common/Card'
import Button from '../common/Button'
import Modal from '../common/Modal'
import toast from 'react-hot-toast'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend)

interface UserHourData {
  portfolios: Set<string>
  issues: Issue[]
  activeIssues: Issue[]
}

interface UserMatrixData {
  userName: string
  displayName: string
  email: string
  totalPortfolios: number
  hours: { [hour: number]: UserHourData }
}

const CoverageMatrix: React.FC = () => {
  const { user: currentUser } = useAuth()
  const [userSearch, setUserSearch] = useState('')
  const [hourFilter, setHourFilter] = useState<string>('all')
  const [issueFilter, setIssueFilter] = useState<'all' | 'active'>('all')
  const [fromDate, setFromDate] = useState(() => {
    const today = new Date()
    return today.toISOString().split('T')[0]
  })
  const [toDate, setToDate] = useState(() => {
    const today = new Date()
    return today.toISOString().split('T')[0]
  })
  const [showCharts, setShowCharts] = useState(true)
  const [selectedUser, setSelectedUser] = useState<UserMatrixData | null>(null)
  const [hoveredCell, setHoveredCell] = useState<{
    userId: string
    hour: number
    position: { top: number; left: number }
  } | null>(null)

  // Fetch portfolios
  const { data: portfolios = [] } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  // Fetch users to map user IDs to emails
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['admin-users'],
    queryFn: adminService.getUsers,
  })

  // Create mapping from user ID to user info (email, full_name)
  const userIdToUser = useMemo(() => {
    const map: { [key: string]: { email: string; full_name: string } } = {}
    users.forEach((user) => {
      map[user.id] = {
        email: user.email,
        full_name: user.full_name || user.email.split('@')[0]
      }
    })
    return map
  }, [users])

  // Fetch issues
  const { data: allIssues = [] } = useQuery<Issue[]>({
    queryKey: ['issues'],
    queryFn: () => issueService.getAll({}),
  })

  // Filter issues by date range
  const filteredIssues = useMemo(() => {
    let filtered = allIssues

    if (fromDate && toDate) {
      const start = new Date(fromDate)
      start.setHours(0, 0, 0, 0)
      const end = new Date(toDate)
      end.setHours(23, 59, 59, 999)

      filtered = filtered.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        return issueDate >= start && issueDate <= end
      })
    }

    // Issue filter
    if (issueFilter === 'active') {
      filtered = filtered.filter(
        (issue) => issue.description && issue.description.toLowerCase() !== 'no issue'
      )
    }

    // Hour filter
    if (hourFilter !== 'all') {
      const hour = parseInt(hourFilter)
      filtered = filtered.filter((issue) => issue.issue_hour === hour)
    }

    return filtered
  }, [allIssues, fromDate, toDate, issueFilter, hourFilter])

  // Build matrix data - portfolios where user marked "All sites checked = Yes"
  const matrixData = useMemo(() => {
    const userMap: { [key: string]: UserMatrixData } = {}

    // Filter portfolios by date range and "All sites checked = Yes"
    const start = fromDate ? new Date(fromDate) : null
    const end = toDate ? new Date(toDate) : null
    if (start) start.setHours(0, 0, 0, 0)
    if (end) end.setHours(23, 59, 59, 999)

    console.log('Coverage Matrix Debug:', {
      totalPortfolios: portfolios.length,
      fromDate,
      toDate,
      usersCount: users.length,
      userIdToUserKeys: Object.keys(userIdToUser),
    })

    let checkedPortfolios = 0
    let skippedNoYes = 0
    let skippedNoUser = 0
    let skippedDateRange = 0
    let skippedUserNotFound = 0
    let addedCount = 0

    portfolios.forEach((portfolio) => {
      // Only count portfolios where all_sites_checked = 'Yes'
      if (portfolio.all_sites_checked !== 'Yes' || !portfolio.all_sites_checked_by) {
        skippedNoYes++
        return
      }

      checkedPortfolios++

      // Check date range - handle both string and Date formats
      if (portfolio.all_sites_checked_date) {
        let checkedDate: Date
        if (typeof portfolio.all_sites_checked_date === 'string') {
          // Handle YYYY-MM-DD format (extract just the date part if there's time)
          const dateStr = portfolio.all_sites_checked_date.split('T')[0]
          checkedDate = new Date(dateStr + 'T00:00:00')
        } else {
          checkedDate = new Date(portfolio.all_sites_checked_date)
        }
        checkedDate.setHours(0, 0, 0, 0)

        // Compare dates (only date part, ignore time)
        if (start) {
          const startDateOnly = new Date(start.getFullYear(), start.getMonth(), start.getDate())
          const checkedDateOnly = new Date(checkedDate.getFullYear(), checkedDate.getMonth(), checkedDate.getDate())
          if (checkedDateOnly < startDateOnly) {
            skippedDateRange++
            return
          }
        }
        if (end) {
          const endDateOnly = new Date(end.getFullYear(), end.getMonth(), end.getDate())
          const checkedDateOnly = new Date(checkedDate.getFullYear(), checkedDate.getMonth(), checkedDate.getDate())
          if (checkedDateOnly > endDateOnly) {
            skippedDateRange++
            return
          }
        }
      } else {
        // If no date is set, skip it (shouldn't happen, but handle gracefully)
        skippedDateRange++
        return
      }

      // Check hour filter
      if (hourFilter !== 'all' && portfolio.all_sites_checked_hour !== undefined) {
        const hour = parseInt(hourFilter)
        if (portfolio.all_sites_checked_hour !== hour) {
          return
        }
      }

      // Get user info from user ID
      const userInfo = userIdToUser[portfolio.all_sites_checked_by]
      if (!userInfo) {
        // If user not found, skip this portfolio (user might have been deleted)
        console.warn(`User not found for ID: ${portfolio.all_sites_checked_by}`, {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          allSitesCheckedBy: portfolio.all_sites_checked_by,
          availableUserIds: Object.keys(userIdToUser),
        })
        skippedUserNotFound++
        return
      }

      const userEmail = userInfo.email
      const displayName = userInfo.full_name || userEmail.split('@')[0] || 'Unknown'

      if (!userMap[userEmail]) {
        userMap[userEmail] = {
          userName: userEmail,
          displayName,
          email: userEmail,
          totalPortfolios: 0,
          hours: {},
        }
      }

      const hour = portfolio.all_sites_checked_hour ?? 0
      if (!userMap[userEmail].hours[hour]) {
        userMap[userEmail].hours[hour] = {
          portfolios: new Set(),
          issues: [],
          activeIssues: [],
        }
      }

      // Add portfolio to this user's hour
      userMap[userEmail].hours[hour].portfolios.add(portfolio.id)
      addedCount++

      // Also add related issues for tooltip/display
      const relatedIssues = filteredIssues.filter(
        (issue) => issue.portfolio_id === portfolio.id && issue.issue_hour === hour
      )
      relatedIssues.forEach((issue) => {
        userMap[userEmail].hours[hour].issues.push(issue)
        if (issue.description && issue.description.toLowerCase() !== 'no issue') {
          userMap[userEmail].hours[hour].activeIssues.push(issue)
        }
      })
    })

    console.log('Coverage Matrix Results:', {
      checkedPortfolios,
      skippedNoYes,
      skippedDateRange,
      skippedUserNotFound,
      addedCount,
      finalUserCount: Object.keys(userMap).length,
    })

    // Calculate total portfolios per user
    Object.values(userMap).forEach((user) => {
      const allPortfolios = new Set<string>()
      Object.values(user.hours).forEach((hourData) => {
        hourData.portfolios.forEach((pid) => allPortfolios.add(pid))
      })
      user.totalPortfolios = allPortfolios.size
    })

    // Filter by user search
    const filteredUsers = Object.values(userMap).filter((user) =>
      user.displayName.toLowerCase().includes(userSearch.toLowerCase()) ||
      user.userName.toLowerCase().includes(userSearch.toLowerCase())
    )

    return filteredUsers.sort((a, b) => b.totalPortfolios - a.totalPortfolios)
  }, [portfolios, filteredIssues, userSearch, fromDate, toDate, hourFilter, userIdToUser])

  // Calculate max count for color coding
  const maxCount = useMemo(() => {
    let max = 0
    matrixData.forEach((user) => {
      Object.values(user.hours).forEach((hourData) => {
        max = Math.max(max, hourData.portfolios.size)
      })
    })
    return max
  }, [matrixData])

  // Get cell color class
  const getCellColorClass = (count: number) => {
    if (count === 0) {
      return 'bg-white border border-gray-200 text-gray-400'
    }
    if (count >= 3) {
      return 'bg-green-600 text-white font-semibold'
    }
    if (count >= 1) {
      return 'bg-green-300 text-green-900 font-semibold'
    }
    return 'bg-white border border-gray-200 text-gray-400'
  }

  // Get cell info for tooltip
  const getCellInfo = (userId: string, hour: number) => {
    const user = matrixData.find((u) => u.userName === userId)
    if (!user) return null

    const hourData = user.hours[hour]
    if (!hourData || hourData.portfolios.size === 0) return null

    const portfolioNames = Array.from(hourData.portfolios)
      .map((pid) => {
        const portfolio = portfolios.find((p) => p.id === pid)
        return portfolio?.name || 'Unknown'
      })
      .filter((name) => name !== 'Unknown')

    return {
      userName: user.displayName,
      hour,
      totalCount: hourData.issues.length,
      portfolios: portfolioNames,
      portfolioCount: hourData.portfolios.size,
    }
  }

  const handleCellMouseEnter = (
    e: React.MouseEvent<HTMLTableCellElement>,
    userId: string,
    hour: number
  ) => {
    const rect = e.currentTarget.getBoundingClientRect()
    setHoveredCell({
      userId,
      hour,
      position: {
        top: rect.top + rect.height / 2,
        left: rect.left + rect.width / 2,
      },
    })
  }

  const handleCellMouseLeave = () => {
    setHoveredCell(null)
  }

  const handleQuickRange = (range: 'today' | 'yesterday' | 'week' | 'month') => {
    const today = new Date()
    const formatDate = (date: Date) => date.toISOString().split('T')[0]

    if (range === 'today') {
      setFromDate(formatDate(today))
      setToDate(formatDate(today))
    } else if (range === 'yesterday') {
      const yesterday = new Date(today)
      yesterday.setDate(yesterday.getDate() - 1)
      setFromDate(formatDate(yesterday))
      setToDate(formatDate(yesterday))
    } else if (range === 'week') {
      const weekAgo = new Date(today)
      weekAgo.setDate(today.getDate() - 7)
      setFromDate(formatDate(weekAgo))
      setToDate(formatDate(today))
    } else if (range === 'month') {
      const monthAgo = new Date(today)
      monthAgo.setMonth(today.getMonth() - 1)
      setFromDate(formatDate(monthAgo))
      setToDate(formatDate(today))
    }
  }

  const handleResetFilters = () => {
    setUserSearch('')
    setHourFilter('all')
    setIssueFilter('all')
    const today = new Date()
    setFromDate(today.toISOString().split('T')[0])
    setToDate(today.toISOString().split('T')[0])
  }

  const handleExportMatrix = () => {
    const csvRows = [
      ['User / Hour', ...Array.from({ length: 24 }, (_, i) => `${i}:00`), 'Total'],
      ...matrixData.map((user) => {
        const row = [`${user.displayName} (${user.totalPortfolios} portfolios summary)`]
        let total = 0
        for (let hour = 0; hour < 24; hour++) {
          const count = user.hours[hour]?.portfolios.size || 0
          row.push(count.toString())
          total += count
        }
        row.push(total.toString())
        return row
      }),
      [
        'Hour Totals',
        ...Array.from({ length: 24 }, (_, hour) => {
          const count = matrixData.reduce(
            (sum, user) => sum + (user.hours[hour]?.portfolios.size || 0),
            0
          )
          return count.toString()
        }),
        matrixData.reduce((sum, user) => {
          return sum + Object.values(user.hours).reduce((s, h) => s + h.portfolios.size, 0)
        }, 0).toString(),
      ],
    ]

    const csvContent = csvRows.map((row) => row.map((cell) => `"${cell}"`).join(',')).join('\n')
    const blob = new Blob([csvContent], { type: 'text/csv' })
    const url = window.URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `coverage_matrix_${new Date().toISOString().split('T')[0]}.csv`
    a.click()
    window.URL.revokeObjectURL(url)
    toast.success('Coverage matrix exported successfully')
  }

  const handleExportChartData = () => {
    const csvRows = [
      ['User', 'Total Portfolios Monitored'],
      ...userCoverageData.map((u) => [u.userName, u.totalPortfolios.toString()]),
    ]

    const csvContent = csvRows.map((row) => row.map((cell) => `"${cell}"`).join(',')).join('\n')
    const blob = new Blob([csvContent], { type: 'text/csv' })
    const url = window.URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `user_coverage_performance_${new Date().toISOString().split('T')[0]}.csv`
    a.click()
    window.URL.revokeObjectURL(url)
    toast.success('Chart data exported successfully')
  }

  // Calculate user coverage for chart - Show ALL users
  const userCoverageData = useMemo(() => {
    return matrixData
      .map((user) => ({
        userName: user.displayName,
        totalPortfolios: user.totalPortfolios,
        userData: user,
      }))
      .sort((a, b) => b.totalPortfolios - a.totalPortfolios)
  }, [matrixData])

  const chartData = {
    labels: userCoverageData.map((u) => u.userName),
    datasets: [
      {
        label: 'Total Portfolios Monitored',
        data: userCoverageData.map((u) => u.totalPortfolios),
        backgroundColor: userCoverageData.map((_, idx) =>
          idx < 3 ? 'rgba(34, 197, 94, 0.9)' : 'rgba(34, 197, 94, 0.5)'
        ),
        borderColor: userCoverageData.map((_, idx) =>
          idx < 3 ? 'rgba(34, 197, 94, 1)' : 'rgba(34, 197, 94, 0.7)'
        ),
        borderWidth: 1,
        barThickness: 30,
        maxBarThickness: 40,
      },
    ],
  }

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        callbacks: {
          label: (context: any) => {
            const user = userCoverageData[context.dataIndex]
            return `${user.userName}: ${user.totalPortfolios} portfolios`
          },
        },
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 2,
          max: 8,
        },
        title: {
          display: true,
          text: 'Total Portfolios Mon',
        },
      },
      x: {
        ticks: {
          maxRotation: 45,
          minRotation: 45,
        },
      },
    },
  }

  const cellInfo = hoveredCell ? getCellInfo(hoveredCell.userId, hoveredCell.hour) : null

  // Calculate totals for summary
  const totalCoverage = useMemo(() => {
    const allPortfolios = new Set<string>()
    matrixData.forEach((user) => {
      Object.values(user.hours).forEach((hourData) => {
        hourData.portfolios.forEach((pid) => allPortfolios.add(pid))
      })
    })
    return {
      portfolios: allPortfolios.size,
      users: matrixData.length,
      hours: 24,
    }
  }, [matrixData])

  // Get user performance details
  const getUserPerformanceDetails = (user: UserMatrixData) => {
    const allPortfolios = new Set<string>()
    const allIssues = new Set<string>()
    const activeIssues: Issue[] = []
    const missedAlerts: Issue[] = []
    const hourlyBreakdown: { hour: number; portfolios: number; issues: number; activeIssues: number }[] = []

    Object.entries(user.hours).forEach(([hourStr, hourData]) => {
      const hour = parseInt(hourStr)
      hourData.portfolios.forEach((pid) => allPortfolios.add(pid))
      hourData.issues.forEach((issue) => allIssues.add(issue.id))
      activeIssues.push(...hourData.activeIssues)
      hourData.issues.forEach((issue) => {
        if (issue.missed_by && issue.missed_by.length > 0) {
          missedAlerts.push(issue)
        }
      })

      hourlyBreakdown.push({
        hour,
        portfolios: hourData.portfolios.size,
        issues: hourData.issues.length,
        activeIssues: hourData.activeIssues.length,
      })
    })

    hourlyBreakdown.sort((a, b) => a.hour - b.hour)

    const portfolioNames = Array.from(allPortfolios)
      .map((pid) => portfolios.find((p) => p.id === pid)?.name)
      .filter((name) => name) as string[]

    const healthySites = allIssues.size - activeIssues.length

    return {
      totalCount: allIssues.size,
      activeIssues: activeIssues.length,
      healthySites,
      missedAlerts: missedAlerts.length,
      totalPortfoliosMonitored: allPortfolios.size,
      monitoringActiveHours: Object.keys(user.hours).length,
      portfolios: portfolioNames,
      hourlyBreakdown,
    }
  }

  return (
    <div className="space-y-6">
      {/* Date Filters Section - At Top */}
      <div>
        {/* Green Banner */}
        <div className="bg-green-600 text-white py-6 px-6 rounded-t-lg shadow-md">
          <h1 className="text-3xl font-bold mb-1">Coverage Matrix</h1>
          <p className="text-green-100 text-sm">
            Track portfolio coverage by user and hour. Use filters to analyze specific date ranges and hours.
          </p>
        </div>

        {/* Filter Card */}
        <Card className="rounded-t-none">
          <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
              <div className="relative">
                <svg
                  className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                  />
                </svg>
                <input
                  type="text"
                  value={userSearch}
                  onChange={(e) => setUserSearch(e.target.value)}
                  placeholder="Type user name..."
                  className="w-full pl-10 pr-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <select
                  value={hourFilter}
                  onChange={(e) => setHourFilter(e.target.value)}
                  className="w-full px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="all">All Hours</option>
                  {Array.from({ length: 24 }, (_, i) => (
                    <option key={i} value={i.toString()}>
                      {i}:00
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <select
                  value={issueFilter}
                  onChange={(e) => setIssueFilter(e.target.value as 'all' | 'active')}
                  className="w-full px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="all">All Issues (Coverage)</option>
                  <option value="active">Active Issues Only</option>
                </select>
              </div>

              <div className="relative">
                <input
                  type="date"
                  value={fromDate}
                  onChange={(e) => setFromDate(e.target.value)}
                  className="w-full px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <svg
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"
                  />
                </svg>
              </div>

              <div className="relative">
                <input
                  type="date"
                  value={toDate}
                  onChange={(e) => setToDate(e.target.value)}
                  className="w-full px-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <svg
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"
                  />
                </svg>
              </div>
            </div>

            <div className="flex items-center gap-2 flex-wrap">
              <span className="text-sm font-medium text-gray-700">Quick Range:</span>
              <button
                onClick={() => handleQuickRange('today')}
                className={`px-3 py-1.5 text-sm rounded-md transition-all ${
                  fromDate === toDate && fromDate === new Date().toISOString().split('T')[0]
                    ? 'bg-green-600 text-white shadow-md'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Today
              </button>
              <button
                onClick={() => handleQuickRange('yesterday')}
                className="px-3 py-1.5 text-sm rounded-md transition-all bg-gray-100 text-gray-700 hover:bg-gray-200"
              >
                Yesterday
              </button>
              <button
                onClick={() => handleQuickRange('week')}
                className="px-3 py-1.5 text-sm rounded-md transition-all bg-gray-100 text-gray-700 hover:bg-gray-200"
              >
                Week
              </button>
              <button
                onClick={() => handleQuickRange('month')}
                className="px-3 py-1.5 text-sm rounded-md transition-all bg-gray-100 text-gray-700 hover:bg-gray-200"
              >
                Month
              </button>
              <Button variant="secondary" size="sm" onClick={handleResetFilters}>
                Reset All Filters
              </Button>
            </div>
          </div>
        </Card>
      </div>

      {/* User Coverage Performance Section - Below Filters */}
      <div>
        {/* Green Banner */}
        <div className="bg-green-600 text-white py-6 px-6 rounded-t-lg shadow-md">
          <h2 className="text-3xl font-bold mb-1">User Coverage Performance</h2>
          <p className="text-green-100 text-sm">
            Top performers by portfolio coverage. Chart shows users who have monitored portfolios based on issues logged with "Monitored By" field.
          </p>
        </div>

        {/* Chart Card */}
        <Card className="rounded-t-none">
          <div className="flex items-center justify-between mb-4">
            <div className="relative flex-1 max-w-md">
              <svg
                className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                />
              </svg>
              <input
                type="text"
                placeholder="Search user by name..."
                className="w-full pl-10 pr-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <Button
              variant="primary"
              size="sm"
              onClick={handleExportChartData}
              style={{ backgroundColor: '#76ab3f' }}
              disabled={userCoverageData.length === 0}
            >
              Export Chart Data
            </Button>
          </div>

          {userCoverageData.length > 0 ? (
            <>
              <div style={{ height: '400px' }}>
                <Bar data={chartData} options={chartOptions} />
              </div>
              <div className="flex items-center justify-center gap-4 mt-4 text-xs">
                <div className="flex items-center gap-2">
                  <div className="w-3 h-3 bg-green-600 rounded-full"></div>
                  <span className="text-gray-600">Top 3 Performers</span>
                </div>
                <div className="flex items-center gap-2">
                  <div className="w-3 h-3 bg-green-300 rounded-full"></div>
                  <span className="text-gray-600">Other Users</span>
                </div>
              </div>
            </>
          ) : (
            <div className="text-center py-12">
              <div className="text-gray-500 mb-2">No data available for chart</div>
              <div className="text-sm text-gray-400 mt-2">
                <p className="mb-1">The chart requires:</p>
                <ul className="list-disc list-inside space-y-1 text-left max-w-md mx-auto">
                  <li>Issues logged within the selected date range</li>
                  <li>Issues with "Monitored By" field populated</li>
                  <li>At least one user who has monitored portfolios</li>
                </ul>
                <p className="mt-3 text-xs">
                  Try adjusting your date range or check if issues have the "Monitored By" field filled in.
                </p>
              </div>
            </div>
          )}
        </Card>
      </div>

      {/* Coverage Overview Table - Below Chart */}
      <div>
        {/* Green Banner */}
        <div className="bg-green-600 text-white py-6 px-6 rounded-t-lg shadow-md">
          <h2 className="text-3xl font-bold mb-1">Coverage Overview</h2>
          <p className="text-green-100 text-sm">
            Track how many portfolios each user covered in each hour. Hover over cells to see portfolio details and issues with 'Yes'.
          </p>
        </div>

        <Card className="rounded-t-none">
          <div className="mb-4 flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 mt-1">
                {totalCoverage.portfolios} portfolio coverage(s) across {totalCoverage.users} user(s)  {totalCoverage.hours} hours
              </p>
            </div>
            <div className="flex items-center gap-2">
              <div className="flex items-center gap-4 text-xs">
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-green-600 rounded"></div>
                  <span className="text-gray-600">High coverage</span>
                </div>
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-green-300 rounded"></div>
                  <span className="text-gray-600">Medium coverage</span>
                </div>
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-white border border-gray-200 rounded"></div>
                  <span className="text-gray-600">No coverage</span>
                </div>
              </div>
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase sticky left-0 bg-gray-50 z-10 border-r border-gray-200">
                    User / Hour
                  </th>
                  {Array.from({ length: 24 }, (_, i) => (
                    <th
                      key={i}
                      className="px-2 py-3 text-center text-xs font-semibold text-gray-700 uppercase min-w-[60px]"
                    >
                      {i}:00
                    </th>
                  ))}
                  <th className="px-4 py-3 text-center text-xs font-semibold text-gray-700 uppercase bg-gray-100 border-l border-gray-200">
                    Total Portfolios
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {matrixData.length === 0 ? (
                  <tr>
                    <td colSpan={26} className="px-4 py-8 text-center text-gray-500">
                      No data available
                    </td>
                  </tr>
                ) : (
                  <>
                    {matrixData.map((row) => {
                      return (
                        <tr key={row.userName} className="hover:bg-gray-50">
                          <td
                            className="px-4 py-3 text-sm font-medium text-gray-900 sticky left-0 bg-white z-10 border-r border-gray-200 cursor-pointer hover:text-blue-600"
                            onClick={() => setSelectedUser(row)}
                          >
                            {row.displayName} ({row.totalPortfolios} portfolios summary)
                          </td>
                          {Array.from({ length: 24 }, (_, hour) => {
                            const count = row.hours[hour]?.portfolios.size || 0
                            return (
                              <td
                                key={hour}
                                className={`px-2 py-3 text-center text-xs font-semibold cursor-pointer transition-colors ${getCellColorClass(
                                  count
                                )}`}
                                onMouseEnter={(e) => handleCellMouseEnter(e, row.userName, hour)}
                                onMouseLeave={handleCellMouseLeave}
                              >
                                {count > 0 ? count : ''}
                              </td>
                            )
                          })}
                          <td className="px-4 py-3 text-center text-sm font-bold text-gray-900 bg-gray-100 border-l border-gray-200">
                            {row.totalPortfolios}
                          </td>
                        </tr>
                      )
                    })}
                    {/* Hour Totals Row */}
                    <tr className="bg-gray-100 font-bold">
                      <td className="px-4 py-3 text-sm text-gray-900 sticky left-0 bg-gray-100 z-10 border-r border-gray-200">
                        Hour Totals
                      </td>
                      {Array.from({ length: 24 }, (_, hour) => {
                        const count = matrixData.reduce(
                          (sum, user) => sum + (user.hours[hour]?.portfolios.size || 0),
                          0
                        )
                        return (
                          <td key={hour} className="px-2 py-3 text-center text-xs text-gray-900">
                            {count > 0 ? count : ''}
                          </td>
                        )
                      })}
                      <td className="px-4 py-3 text-center text-sm text-gray-900 bg-gray-200 border-l border-gray-200">
                        {matrixData.reduce((sum, user) => sum + user.totalPortfolios, 0)}
                      </td>
                    </tr>
                  </>
                )}
              </tbody>
            </table>
          </div>
        </Card>
      </div>

      {/* Hover Tooltip */}
      {hoveredCell && cellInfo && (
        <div
          className="fixed z-[9999] w-80 bg-blue-900 text-white text-xs rounded-lg shadow-xl p-4 pointer-events-none"
          style={{
            top: `${hoveredCell.position.top}px`,
            left: `${hoveredCell.position.left}px`,
            transform: 'translate(-50%, -100%)',
            marginTop: '-10px',
          }}
        >
          <div className="font-bold text-sm mb-2 pb-2 border-b border-blue-700">
            {cellInfo.userName} - Hour {cellInfo.hour}:00
          </div>
          <div className="mb-2">
            <div className="font-semibold text-blue-300">
              Total Count (All Issues): {cellInfo.totalCount}
            </div>
          </div>
          <div>
            <div className="font-semibold text-green-300 mb-1">
              Total Portfolios Monitored ({cellInfo.portfolioCount}):
            </div>
            <div className="space-y-1 max-h-32 overflow-y-auto">
              {cellInfo.portfolios.map((portfolio, idx) => (
                <div key={idx} className="text-blue-100 pl-2">
                   {portfolio}
                </div>
              ))}
            </div>
          </div>
          <div
            className="absolute left-1/2 transform -translate-x-1/2 top-full border-4 border-transparent border-t-blue-900"
            style={{ marginTop: '-1px' }}
          ></div>
        </div>
      )}

      {/* Action Buttons */}
      <div className="flex items-center justify-end gap-2">
        <Button variant="secondary" size="sm" onClick={() => setShowCharts(!showCharts)}>
          {showCharts ? 'Hide Charts' : 'Show Charts'}
        </Button>
        <Button variant="primary" size="sm" onClick={handleExportMatrix} style={{ backgroundColor: '#76ab3f' }}>
          Export Matrix
        </Button>
      </div>

      {/* User Performance Details Modal */}
      {selectedUser && (
        <UserPerformanceModal
          user={selectedUser}
          onClose={() => setSelectedUser(null)}
          getUserPerformanceDetails={getUserPerformanceDetails}
        />
      )}
    </div>
  )
}

interface UserPerformanceModalProps {
  user: UserMatrixData
  onClose: () => void
  getUserPerformanceDetails: (user: UserMatrixData) => {
    totalCount: number
    activeIssues: number
    healthySites: number
    missedAlerts: number
    totalPortfoliosMonitored: number
    monitoringActiveHours: number
    portfolios: string[]
    hourlyBreakdown: { hour: number; portfolios: number; issues: number; activeIssues: number }[]
  }
}

const UserPerformanceModal: React.FC<UserPerformanceModalProps> = ({
  user,
  onClose,
  getUserPerformanceDetails,
}) => {
  const details = getUserPerformanceDetails(user)

  const hourlyChartData = {
    labels: details.hourlyBreakdown.map((h) => h.hour.toString()),
    datasets: [
      {
        label: 'Portfolios',
        data: details.hourlyBreakdown.map((h) => h.portfolios),
        backgroundColor: 'rgba(34, 197, 94, 0.8)',
        borderColor: 'rgba(34, 197, 94, 1)',
        borderWidth: 1,
      },
      {
        label: 'Issues',
        data: details.hourlyBreakdown.map((h) => h.issues),
        backgroundColor: 'rgba(59, 130, 246, 0.8)',
        borderColor: 'rgba(59, 130, 246, 1)',
        borderWidth: 1,
      },
      {
        label: 'Active Issues',
        data: details.hourlyBreakdown.map((h) => h.activeIssues),
        backgroundColor: 'rgba(239, 68, 68, 0.8)',
        borderColor: 'rgba(239, 68, 68, 1)',
        borderWidth: 1,
      },
    ],
  }

  const hourlyChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'bottom' as const,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 0.5,
          max: 2,
        },
        title: {
          display: true,
          text: 'Count',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Hour',
        },
      },
    },
  }

  return (
    <Modal isOpen={true} onClose={onClose} title="User Performance Details" size="lg">
      <div className="space-y-6">
        <div>
          <h3 className="text-xl font-bold text-gray-900">{user.displayName}</h3>
        </div>

        {/* Metric Cards */}
        <div className="grid grid-cols-3 gap-4">
          <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
            <div className="text-2xl font-bold text-blue-600">{details.totalCount}</div>
            <div className="text-sm text-gray-600 mt-1">Total Count</div>
          </div>
          <div className="bg-red-50 rounded-lg p-4 border border-red-200">
            <div className="text-2xl font-bold text-red-600">{details.activeIssues}</div>
            <div className="text-sm text-gray-600 mt-1">Active Issues</div>
          </div>
          <div className="bg-green-50 rounded-lg p-4 border border-green-200">
            <div className="text-2xl font-bold text-green-600">{details.healthySites}</div>
            <div className="text-sm text-gray-600 mt-1">Healthy Sites</div>
          </div>
          <div className="bg-orange-50 rounded-lg p-4 border border-orange-200">
            <div className="text-2xl font-bold text-orange-600">{details.missedAlerts}</div>
            <div className="text-sm text-gray-600 mt-1">Missed Alerts</div>
          </div>
          <div className="bg-purple-50 rounded-lg p-4 border border-purple-200">
            <div className="text-2xl font-bold text-purple-600">{details.totalPortfoliosMonitored}</div>
            <div className="text-sm text-gray-600 mt-1">Total Portfolios Monitored</div>
          </div>
          <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
            <div className="text-2xl font-bold text-blue-600">{details.monitoringActiveHours}</div>
            <div className="text-sm text-gray-600 mt-1">Monitoring Active Hours</div>
          </div>
        </div>

        {/* Total Portfolios Monitored */}
        {details.portfolios.length > 0 && (
          <div>
            <h4 className="text-lg font-semibold text-gray-900 mb-3">
              Total Portfolios Monitored ({details.portfolios.length})
            </h4>
            <div className="flex flex-wrap gap-2">
              {details.portfolios.map((portfolio, idx) => (
                <span
                  key={idx}
                  className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium"
                >
                  {portfolio}
                </span>
              ))}
            </div>
          </div>
        )}

        {/* Hourly Breakdown Chart */}
        {details.hourlyBreakdown.length > 0 && (
          <div>
            <h4 className="text-lg font-semibold text-gray-900 mb-3">Hourly Breakdown</h4>
            <div style={{ height: '300px' }}>
              <Bar data={hourlyChartData} options={hourlyChartOptions} />
            </div>
          </div>
        )}

        <div className="flex justify-end pt-4">
          <Button variant="primary" onClick={onClose} style={{ backgroundColor: '#76ab3f' }}>
            Close
          </Button>
        </div>
      </div>
    </Modal>
  )
}

export default CoverageMatrix
