import React, { useState } from 'react'
import QuickPortfolioReference from './QuickPortfolioReference'
import HourlyCoverageAnalysis from './HourlyCoverageAnalysis'
import PortfolioForm from '../portfolio/PortfolioForm'
import { portfolioService } from '../../services/portfolioService'
import { CreatePortfolioData, UpdatePortfolioData } from '../../types/portfolio.types'
import { useQueryClient } from '@tanstack/react-query'
import toast from 'react-hot-toast'
import Button from '../common/Button'

const Dashboard: React.FC = () => {
  const [showCreateModal, setShowCreateModal] = useState(false)
  const queryClient = useQueryClient()

  const handleCreatePortfolio = async (data: CreatePortfolioData | UpdatePortfolioData) => {
    try {
      // Since we're only creating, ensure all required fields are present
      if (!data.name) {
        toast.error('Portfolio name is required')
        return
      }
      const createData: CreatePortfolioData = {
        name: data.name,
        subtitle: data.subtitle,
        site_range: data.site_range,
      }
      await portfolioService.create(createData)
      queryClient.invalidateQueries({ queryKey: ['portfolios'] })
      queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Portfolio created successfully!')
      setShowCreateModal(false)
    } catch (error: any) {
      toast.error(error.response?.data?.error || 'Failed to create portfolio')
      throw error
    }
  }

  return (
    <div className="space-y-6 w-full">
      <QuickPortfolioReference />

      {/* Portfolio Creation Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Create New Portfolio</h2>
            <p className="text-gray-600 text-sm mt-1">Add a new portfolio to track issues</p>
          </div>
          <Button onClick={() => setShowCreateModal(true)}>
            + Create Portfolio
          </Button>
        </div>
      </div>

      <HourlyCoverageAnalysis />

      {/* Create Portfolio Modal */}
      <PortfolioForm
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSubmit={handleCreatePortfolio}
        portfolio={null}
      />
    </div>
  )
}

export default Dashboard
