import React, { useState } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { adminService } from '../../services/adminService'
import { portfolioService } from '../../services/portfolioService'
import { analyticsService } from '../../services/analyticsService'
import { User, CreateUserData } from '../../types/user.types'
import { Portfolio } from '../../types/portfolio.types'
import toast from 'react-hot-toast'
import UserManagement from './UserManagement'
import PortfolioForm from '../portfolio/PortfolioForm'
import Badge from '../common/Badge'
import Spinner from '../common/Spinner'
import EmptyState from '../common/EmptyState'
import Button from '../common/Button'
import Input from '../common/Input'
import Modal from '../common/Modal'

const AdminPanel: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'portfolios' | 'users' | 'personnel' | 'locks' | 'logs' | 'matrix'>(
    'portfolios'
  )

  return (
    <div className="space-y-6">
      <h1 className="text-3xl font-bold text-gray-800">Admin Panel</h1>

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="flex space-x-8">
          {[
            { id: 'portfolios', label: 'Portfolios', icon: 'ðŸ“' },
            { id: 'users', label: 'Users', icon: 'ðŸ‘¥' },
            { id: 'personnel', label: 'Personnel', icon: 'ðŸ‘¤' },
            { id: 'locks', label: 'Active Locks', icon: 'ðŸ”’' },
            { id: 'logs', label: 'Admin Logs', icon: 'ðŸ“' },
            { id: 'matrix', label: 'Coverage Matrix', icon: 'ðŸ“Š' },
          ].map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id as any)}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <span className="mr-2">{tab.icon}</span>
              {tab.label}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="mt-6">
        {activeTab === 'portfolios' && <PortfoliosTab />}
        {activeTab === 'users' && <UsersTab />}
        {activeTab === 'personnel' && <PersonnelTab />}
        {activeTab === 'locks' && <LocksTab />}
        {activeTab === 'logs' && <LogsTab />}
        {activeTab === 'matrix' && <MatrixTab />}
      </div>
    </div>
  )
}

const PortfoliosTab: React.FC = () => {
  const [showModal, setShowModal] = useState(false)
  const [editingPortfolio, setEditingPortfolio] = useState<Portfolio | null>(null)
  const queryClient = useQueryClient()

  const { data: portfolios, isLoading } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  const createMutation = useMutation({
    mutationFn: portfolioService.create,
    onSuccess: async () => {
      // Invalidate both queries to update Admin Panel and Dashboard
      await queryClient.invalidateQueries({ queryKey: ['portfolios'] })
      await queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      // Force refetch dashboard query immediately
      await queryClient.refetchQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Portfolio created successfully')
      setShowModal(false)
      setEditingPortfolio(null)
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to create portfolio')
    },
  })

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) => portfolioService.update(id, data),
    onSuccess: async () => {
      // Invalidate both queries to update Admin Panel and Dashboard
      await queryClient.invalidateQueries({ queryKey: ['portfolios'] })
      await queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      // Force refetch dashboard query immediately
      await queryClient.refetchQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Portfolio updated successfully')
      setShowModal(false)
      setEditingPortfolio(null)
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to update portfolio')
    },
  })

  const deleteMutation = useMutation({
    mutationFn: portfolioService.delete,
    onSuccess: async () => {
      // Invalidate both queries to update Admin Panel and Dashboard
      await queryClient.invalidateQueries({ queryKey: ['portfolios'] })
      await queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      // Force refetch dashboard query immediately
      await queryClient.refetchQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Portfolio deleted successfully')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to delete portfolio')
    },
  })

  const handleSubmit = (data: any) => {
    if (editingPortfolio) {
      updateMutation.mutate({ id: editingPortfolio.id, data })
    } else {
      createMutation.mutate(data)
    }
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Spinner size="lg" />
      </div>
    )
  }

  return (
    <div>
      <div className="flex justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Portfolio Management</h2>
          <p className="text-gray-600 mt-1">Create, edit, and manage portfolios</p>
        </div>
        <Button onClick={() => { setEditingPortfolio(null); setShowModal(true) }}>
          + Add Portfolio
        </Button>
      </div>

      {portfolios && portfolios.length === 0 ? (
        <EmptyState icon="ðŸ“" title="No portfolios yet" description="Create your first portfolio to get started" />
      ) : (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Subtitle</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Site Range</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {portfolios?.map((p) => (
                <tr key={p.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{p.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{p.subtitle || '-'}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{p.site_range || '-'}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    <span
                      className={`px-2 py-1 rounded text-xs ${
                        p.all_sites_checked === 'Yes'
                          ? 'bg-green-100 text-green-800'
                          : p.all_sites_checked === 'No'
                          ? 'bg-red-100 text-red-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}
                    >
                      {p.all_sites_checked || 'Pending'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="secondary"
                        onClick={() => {
                          setEditingPortfolio(p)
                          setShowModal(true)
                        }}
                      >
                        Edit
                      </Button>
                      <Button
                        size="sm"
                        variant="danger"
                        onClick={() => {
                          if (window.confirm(`Are you sure you want to delete "${p.name}"?`)) {
                            deleteMutation.mutate(p.id)
                          }
                        }}
                      >
                        Delete
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <PortfolioForm
          isOpen={showModal}
          onClose={() => {
            setShowModal(false)
            setEditingPortfolio(null)
          }}
          onSubmit={handleSubmit}
          portfolio={editingPortfolio}
          isLoading={createMutation.isPending || updateMutation.isPending}
        />
      )}
    </div>
  )
}

const UsersTab: React.FC = () => {
  return <UserManagement />
}

const PersonnelTab: React.FC = () => {
  const [showModal, setShowModal] = useState(false)
  const [editingPersonnel, setEditingPersonnel] = useState<any>(null)
  const [formData, setFormData] = useState({ name: '', role: 'monitor' })
  const queryClient = useQueryClient()

  const { data: personnel, isLoading } = useQuery({
    queryKey: ['personnel'],
    queryFn: adminService.getPersonnel,
  })

  const createMutation = useMutation({
    mutationFn: adminService.createPersonnel,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['personnel'] })
      toast.success('Personnel added successfully')
      setShowModal(false)
      setFormData({ name: '', role: 'monitor' })
      setEditingPersonnel(null)
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to add personnel')
    },
  })

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) => adminService.updatePersonnel(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['personnel'] })
      toast.success('Personnel updated successfully')
      setShowModal(false)
      setFormData({ name: '', role: 'monitor' })
      setEditingPersonnel(null)
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to update personnel')
    },
  })

  const deleteMutation = useMutation({
    mutationFn: adminService.deletePersonnel,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['personnel'] })
      toast.success('Personnel deleted successfully')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to delete personnel')
    },
  })

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    if (editingPersonnel) {
      updateMutation.mutate({ id: editingPersonnel.id, data: formData })
    } else {
      createMutation.mutate(formData)
    }
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Spinner size="lg" />
      </div>
    )
  }

  return (
    <div>
      <div className="flex justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Monitored Personnel</h2>
          <p className="text-gray-600 mt-1">Manage the list of people who monitor sites</p>
        </div>
        <Button onClick={() => { setEditingPersonnel(null); setFormData({ name: '', role: 'monitor' }); setShowModal(true) }}>
          + Add Personnel
        </Button>
      </div>

      {personnel && personnel.length === 0 ? (
        <EmptyState icon="ðŸ‘¤" title="No personnel yet" description="Add monitored personnel to get started" />
      ) : (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Role</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {personnel?.map((p) => (
                <tr key={p.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{p.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{p.role}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span
                      className={`px-2 py-1 rounded text-xs ${
                        p.is_active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                      }`}
                    >
                      {p.is_active ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="secondary"
                        onClick={() => {
                          setEditingPersonnel(p)
                          setFormData({ name: p.name, role: p.role })
                          setShowModal(true)
                        }}
                      >
                        Edit
                      </Button>
                      <Button
                        size="sm"
                        variant="danger"
                        onClick={() => {
                          if (window.confirm(`Are you sure you want to delete "${p.name}"?`)) {
                            deleteMutation.mutate(p.id)
                          }
                        }}
                      >
                        Delete
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <Modal
          isOpen={showModal}
          onClose={() => {
            setShowModal(false)
            setEditingPersonnel(null)
            setFormData({ name: '', role: 'monitor' })
          }}
          title={editingPersonnel ? 'Edit Personnel' : 'Add Personnel'}
          size="md"
        >
          <form onSubmit={handleSubmit} className="space-y-4">
            <Input
              label="Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
              placeholder="e.g., John Doe"
            />
            <Input
              label="Role"
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
              placeholder="e.g., monitor, supervisor"
            />
            <div className="flex gap-3 pt-4">
              <Button type="submit" isLoading={createMutation.isPending || updateMutation.isPending} className="flex-1">
                {editingPersonnel ? 'Update' : 'Add'} Personnel
              </Button>
              <Button
                type="button"
                variant="secondary"
                onClick={() => {
                  setShowModal(false)
                  setEditingPersonnel(null)
                  setFormData({ name: '', role: 'monitor' })
                }}
                className="flex-1"
              >
                Cancel
              </Button>
            </div>
          </form>
        </Modal>
      )}
    </div>
  )
}

const LocksTab: React.FC = () => {
  const queryClient = useQueryClient()

  const { data: locks, isLoading } = useQuery({
    queryKey: ['locks'],
    queryFn: adminService.getLocks,
  })

  const unlockMutation = useMutation({
    mutationFn: adminService.unlockPortfolio,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['locks'] })
      toast.success('Portfolio unlocked successfully')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to unlock portfolio')
    },
  })

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Spinner size="lg" />
      </div>
    )
  }

  return (
    <div>
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Active Portfolio Locks</h2>
        <p className="text-gray-600 mt-1">View and manage portfolio locks</p>
      </div>

      {locks && locks.length === 0 ? (
        <EmptyState icon="ðŸ”“" title="No active locks" description="No portfolios are currently locked" />
      ) : (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Portfolio</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Hour</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Reserved At</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Expires At</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {locks?.map((lock) => (
                <tr key={lock.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {lock.portfolio?.name || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{lock.issue_hour}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(lock.reserved_at).toLocaleString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(lock.expires_at).toLocaleString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm">
                    <Button
                      size="sm"
                      variant="danger"
                      onClick={() => {
                        if (window.confirm('Are you sure you want to unlock this portfolio?')) {
                          unlockMutation.mutate(lock.portfolio_id)
                        }
                      }}
                    >
                      Unlock
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}

const LogsTab: React.FC = () => {
  const [showModal, setShowModal] = useState(false)
  const [formData, setFormData] = useState({
    actionType: '',
    actionDescription: '',
  })
  const queryClient = useQueryClient()

  const { data: logs, isLoading } = useQuery({
    queryKey: ['admin-logs'],
    queryFn: adminService.getLogs,
  })

  const createLogMutation = useMutation({
    mutationFn: adminService.createLog,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['admin-logs'] })
      toast.success('Log entry created successfully')
      setShowModal(false)
      setFormData({ actionType: '', actionDescription: '' })
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to create log entry')
    },
  })

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Spinner size="lg" />
      </div>
    )
  }

  return (
    <div>
      <div className="flex justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Admin Activity Logs</h2>
          <p className="text-gray-600 mt-1">View activity history and add custom log entries</p>
        </div>
        <Button onClick={() => setShowModal(true)}>+ Add Log Entry</Button>
      </div>

      {logs && logs.length === 0 ? (
        <EmptyState icon="ðŸ“" title="No logs yet" description="Admin activity will appear here" />
      ) : (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Admin</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Action</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Description</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {logs?.map((log) => (
                <tr key={log.log_id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{log.admin_name}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <Badge variant="info">{log.action_type}</Badge>
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-500">{log.action_description}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(log.created_at).toLocaleString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-2xl font-bold mb-4">Add Log Entry</h2>
            <form
              onSubmit={(e) => {
                e.preventDefault()
                createLogMutation.mutate(formData)
              }}
              className="space-y-4"
            >
              <Input
                label="Action Type"
                value={formData.actionType}
                onChange={(e) => setFormData({ ...formData, actionType: e.target.value })}
                required
                placeholder="e.g., Manual Entry"
              />
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
                <textarea
                  value={formData.actionDescription}
                  onChange={(e) => setFormData({ ...formData, actionDescription: e.target.value })}
                  rows={4}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="flex gap-3">
                <Button type="submit" isLoading={createLogMutation.isPending} className="flex-1">
                  Add Log
                </Button>
                <Button type="button" variant="secondary" onClick={() => setShowModal(false)} className="flex-1">
                  Cancel
                </Button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  )
}

const MatrixTab: React.FC = () => {
  const { data: matrix, isLoading } = useQuery({
    queryKey: ['coverage-matrix'],
    queryFn: () => analyticsService.getCoverageMatrix(),
  })

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Spinner size="lg" />
      </div>
    )
  }

  return (
    <div>
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Coverage Matrix</h2>
        <p className="text-gray-600 mt-1">Portfolio monitoring matrix (Portfolio Ã— Hour)</p>
      </div>

      {matrix && (
        <div className="bg-white rounded-lg shadow overflow-x-auto">
          <table className="min-w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase sticky left-0 bg-gray-50 z-10">
                  Portfolio
                </th>
                {Array.from({ length: 24 }, (_, i) => (
                  <th key={i} className="px-2 py-3 text-center text-xs font-medium text-gray-500 uppercase min-w-[40px]">
                    {i}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {matrix.portfolios?.map((portfolio) => (
                <tr key={portfolio.id} className="hover:bg-gray-50">
                  <td className="px-4 py-3 text-sm font-medium text-gray-900 sticky left-0 bg-white z-10">
                    {portfolio.name}
                  </td>
                  {Array.from({ length: 24 }, (_, hour) => {
                    const count = matrix.matrix[portfolio.id]?.[hour] || 0
                    return (
                      <td
                        key={hour}
                        className={`px-2 py-3 text-center text-xs ${
                          count === 0
                            ? 'bg-gray-50'
                            : count === 1
                            ? 'bg-yellow-100'
                            : count === 2
                            ? 'bg-orange-100'
                            : 'bg-red-100'
                        }`}
                        title={`${portfolio.name} - Hour ${hour}: ${count} issue(s)`}
                      >
                        {count > 0 ? count : ''}
                      </td>
                    )
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}

export default AdminPanel
