import React, { useState, useMemo } from 'react'
import { useQuery } from '@tanstack/react-query'
import { Bar } from 'react-chartjs-2'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js'
import { issueService } from '../../services/issueService'
import { portfolioService } from '../../services/portfolioService'
import { analyticsService } from '../../services/analyticsService'
import { Issue } from '../../types/issue.types'
import { Portfolio } from '../../types/portfolio.types'
import { HourlyCoverage } from '../../types/analytics.types'
import Card from '../common/Card'
import Button from '../common/Button'
import toast from 'react-hot-toast'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement)

interface PerformanceAnalyticsProps {
  dateRange?: 'today' | 'yesterday' | 'week' | 'custom'
  startDate?: string
  endDate?: string
}

const PerformanceAnalytics: React.FC<PerformanceAnalyticsProps> = ({
  dateRange = 'today',
  startDate,
  endDate,
}) => {
  const [selectedRange, setSelectedRange] = useState<'today' | 'yesterday' | 'week' | 'custom'>(
    dateRange
  )
  const [customStartDate, setCustomStartDate] = useState(startDate || '')
  const [customEndDate, setCustomEndDate] = useState(endDate || '')

  // Fetch portfolios
  const { data: portfolios = [] } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  // Fetch issues
  const { data: issues = [] } = useQuery<Issue[]>({
    queryKey: ['issues'],
    queryFn: () => issueService.getAll({}),
  })

  // Fetch hourly coverage
  const { data: hourlyCoverage = [] } = useQuery<HourlyCoverage[]>({
    queryKey: ['hourly-coverage-range', selectedRange, customStartDate, customEndDate],
    queryFn: async () => {
      const today = new Date()
      let start: string | undefined
      let end: string | undefined

      if (selectedRange === 'today') {
        const todayStr = today.toISOString().split('T')[0]
        start = todayStr
        end = todayStr
      } else if (selectedRange === 'yesterday') {
        const yesterday = new Date(today)
        yesterday.setDate(yesterday.getDate() - 1)
        const yesterdayStr = yesterday.toISOString().split('T')[0]
        start = yesterdayStr
        end = yesterdayStr
      } else if (selectedRange === 'week') {
        const weekAgo = new Date(today)
        weekAgo.setDate(today.getDate() - 7)
        start = weekAgo.toISOString().split('T')[0]
        end = today.toISOString().split('T')[0]
      } else if (selectedRange === 'custom' && customStartDate && customEndDate) {
        start = customStartDate
        end = customEndDate
      } else {
        const todayStr = today.toISOString().split('T')[0]
        start = todayStr
        end = todayStr
      }

      return analyticsService.getHourlyCoverageWithDateRange(start, end)
    },
  })

  // Calculate analytics
  const analytics = useMemo(() => {
    const today = new Date()
    today.setHours(0, 0, 0, 0)

    // Filter issues based on date range
    let filteredIssues = issues

    if (selectedRange === 'today') {
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        issueDate.setHours(0, 0, 0, 0)
        return issueDate.getTime() === today.getTime()
      })
    } else if (selectedRange === 'yesterday') {
      const yesterday = new Date(today)
      yesterday.setDate(yesterday.getDate() - 1)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        issueDate.setHours(0, 0, 0, 0)
        return issueDate.getTime() === yesterday.getTime()
      })
    } else if (selectedRange === 'week') {
      const weekAgo = new Date(today)
      weekAgo.setDate(today.getDate() - 7)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        return issueDate >= weekAgo
      })
    } else if (selectedRange === 'custom' && customStartDate && customEndDate) {
      const start = new Date(customStartDate)
      const end = new Date(customEndDate)
      end.setHours(23, 59, 59, 999)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        return issueDate >= start && issueDate <= end
      })
    }

    const totalPortfolios = portfolios.length || 26
    const uniquePortfoliosToday = new Set(filteredIssues.map((i) => i.portfolio_id))
    const overallCoverage = totalPortfolios > 0 ? (uniquePortfoliosToday.size / totalPortfolios) * 100 : 0

    // Calculate active hours (hours with at least one issue)
    const activeHoursSet = new Set(filteredIssues.map((i) => i.issue_hour))
    const activeHours = activeHoursSet.size

    // Calculate average hourly coverage
    const avgHourlyCoverage =
      hourlyCoverage.length > 0
        ? hourlyCoverage.reduce((sum, h) => sum + h.coverage, 0) / hourlyCoverage.length
        : 0
    const performanceScore = Math.round(avgHourlyCoverage / 10)

    // Find peak and lowest coverage hours
    const peakHour = hourlyCoverage.reduce(
      (max, current) => (current.coverage > max.coverage ? current : max),
      hourlyCoverage[0] || { hour: 0, coverage: 0 }
    )
    const lowestHour = hourlyCoverage.reduce(
      (min, current) => (current.coverage < min.coverage ? current : min),
      hourlyCoverage[0] || { hour: 0, coverage: 0 }
    )

    // Calculate hours with 100% coverage
    const fullCoverageHours = hourlyCoverage.filter((h) => h.coverage === 100).length

    // Calculate per-user stats
    const userStatsMap: {
      [key: string]: {
        user: string
        displayName: string
        issues: number
        issuesYes: number
        portfoliosCount: number
        hoursCount: number
        missedAlerts: number
      }
    } = {}

    filteredIssues.forEach((issue) => {
      const monitoredBy = issue.monitored_by?.[0] || 'Unknown'
      const username = monitoredBy.split('@')[0]
      const displayName = username || 'Unknown'

      if (!userStatsMap[monitoredBy]) {
        userStatsMap[monitoredBy] = {
          user: monitoredBy,
          displayName,
          issues: 0,
          issuesYes: 0,
          portfoliosCount: 0,
          hoursCount: 0,
          missedAlerts: 0,
        }
      }

      userStatsMap[monitoredBy].issues++
      if (issue.description && issue.description.toLowerCase() !== 'no issue') {
        userStatsMap[monitoredBy].issuesYes++
      }
      if (issue.missed_by && issue.missed_by.length > 0) {
        userStatsMap[monitoredBy].missedAlerts += issue.missed_by.length
      }
    })

    // Calculate unique portfolios and hours per user
    const userPortfoliosMap: { [key: string]: Set<string> } = {}
    const userHoursMap: { [key: string]: Set<number> } = {}

    filteredIssues.forEach((issue) => {
      const monitoredBy = issue.monitored_by?.[0] || 'Unknown'
      if (!userPortfoliosMap[monitoredBy]) {
        userPortfoliosMap[monitoredBy] = new Set()
        userHoursMap[monitoredBy] = new Set()
      }
      userPortfoliosMap[monitoredBy].add(issue.portfolio_id)
      userHoursMap[monitoredBy].add(issue.issue_hour)
    })

    Object.keys(userStatsMap).forEach((user) => {
      userStatsMap[user].portfoliosCount = userPortfoliosMap[user]?.size || 0
      userStatsMap[user].hoursCount = userHoursMap[user]?.size || 0
    })

    const perUserStats = Object.values(userStatsMap)

    // Leaderboards
    const byIssuesYes = [...perUserStats].sort(
      (a, b) => b.issuesYes - a.issuesYes || b.issues - a.issues
    )
    const byCoverage = [...perUserStats].sort(
      (a, b) => b.portfoliosCount - a.portfoliosCount || b.issues - a.issues
    )
    const byHours = [...perUserStats].sort(
      (a, b) => b.hoursCount - a.hoursCount || b.issues - a.issues
    )

    return {
      peakHour,
      lowestHour,
      fullCoverageHours,
      overallCoverage,
      performanceScore,
      portfoliosChecked: uniquePortfoliosToday.size,
      totalPortfolios,
      totalIssuesLogged: filteredIssues.length,
      activeHours,
      perUserStats,
      leaderboard: {
        issuesYes: byIssuesYes.slice(0, 7),
        coverage: byCoverage.slice(0, 7),
        hours: byHours.slice(0, 7),
      },
    }
  }, [issues, portfolios, hourlyCoverage, selectedRange, customStartDate, customEndDate])

  const getPerformanceLabel = (score: number) => {
    if (score >= 8) return { text: 'Excellent', color: 'text-green-600', bgColor: 'bg-green-50', borderColor: 'border-green-200' }
    if (score >= 5) return { text: 'Good', color: 'text-lime-600', bgColor: 'bg-lime-50', borderColor: 'border-lime-200' }
    if (score >= 3) return { text: 'Fair', color: 'text-yellow-600', bgColor: 'bg-yellow-50', borderColor: 'border-yellow-200' }
    return { text: 'Needs Improvement', color: 'text-red-600', bgColor: 'bg-red-50', borderColor: 'border-red-200' }
  }

  const performanceLabel = getPerformanceLabel(analytics.performanceScore)

  const handleExportCSV = () => {
    const csvRows = [
      ['User', 'Total Count', 'Total Portfolios Monitored', 'Monitoring Active Hours', 'Missed Alerts'],
      ...analytics.perUserStats.map((u) => [
        u.displayName,
        u.issues.toString(),
        u.portfoliosCount.toString(),
        u.hoursCount.toString(),
        u.missedAlerts.toString(),
      ]),
    ]

    const csvContent = csvRows.map((row) => row.join(',')).join('\n')
    const blob = new Blob([csvContent], { type: 'text/csv' })
    const url = window.URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `performance_analytics_${new Date().toISOString().split('T')[0]}.csv`
    a.click()
    window.URL.revokeObjectURL(url)
    toast.success('Performance analytics exported successfully')
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Performance Analytics</h1>
          <p className="text-gray-600 mt-1">Comprehensive insights into monitoring coverage and user performance</p>
        </div>
        <Button onClick={handleExportCSV} variant="primary">
          Export to CSV
        </Button>
      </div>

      {/* Date Range Filters */}
      <Card>
        <div className="space-y-4">
          <h2 className="text-lg font-semibold text-gray-900">Date Range</h2>
          <div className="flex items-center gap-4 flex-wrap">
            <button
              onClick={() => setSelectedRange('today')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'today'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'today' ? { backgroundColor: '#76ab3f' } : {}}
            >
              Today
            </button>
            <button
              onClick={() => setSelectedRange('yesterday')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'yesterday'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'yesterday' ? { backgroundColor: '#76ab3f' } : {}}
            >
              Yesterday
            </button>
            <button
              onClick={() => setSelectedRange('week')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'week'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'week' ? { backgroundColor: '#76ab3f' } : {}}
            >
              Last 7 Days
            </button>
            <button
              onClick={() => setSelectedRange('custom')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'custom'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'custom' ? { backgroundColor: '#76ab3f' } : {}}
            >
              Custom Range
            </button>
          </div>

          {selectedRange === 'custom' && (
            <div className="flex items-center gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
                <input
                  type="date"
                  value={customStartDate}
                  onChange={(e) => setCustomStartDate(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
                <input
                  type="date"
                  value={customEndDate}
                  onChange={(e) => setCustomEndDate(e.target.value)}
                  className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
          )}
        </div>
      </Card>

      {/* Main Metrics */}
      <div className="grid grid-cols-4 gap-3 max-w-3xl">
        <MetricCard
          label="Overall Coverage"
          value={`${Math.round(analytics.overallCoverage)}%`}
          accent="text-green-600"
          highlight="bg-gradient-to-r from-emerald-50 to-green-100"
        />
        <MetricCard
          label="Portfolios Checked"
          value={analytics.portfoliosChecked}
          highlight="bg-gradient-to-r from-blue-50 to-sky-100"
        />
        <MetricCard
          label="Monitoring Active Hours"
          value={`${analytics.activeHours}/24`}
          highlight="bg-gradient-to-r from-amber-50 to-orange-100"
        />
        <MetricCard
          label="Issues Logged"
          value={analytics.totalIssuesLogged}
          highlight="bg-gradient-to-r from-purple-50 to-indigo-100"
        />
      </div>

      {/* Performance Score */}
      <Card>
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-gray-900 mb-2">Performance Score</h2>
            <div className={`inline-flex items-center px-4 py-2 rounded-lg border-2 ${performanceLabel.bgColor} ${performanceLabel.borderColor}`}>
              <span className={`text-2xl font-bold ${performanceLabel.color}`}>
                {analytics.performanceScore}/10
              </span>
              <span className={`ml-3 text-lg font-semibold ${performanceLabel.color}`}>
                {performanceLabel.text}
              </span>
            </div>
          </div>
          <div className="text-right">
            <p className="text-sm text-gray-600">Average Hourly Coverage</p>
            <p className="text-2xl font-bold text-gray-900">
              {Math.round(
                hourlyCoverage.length > 0
                  ? hourlyCoverage.reduce((sum, h) => sum + h.coverage, 0) / hourlyCoverage.length
                  : 0
              )}
              %
            </p>
          </div>
        </div>
      </Card>

      {/* Coverage Statistics */}
      <Card>
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Coverage Statistics</h2>
        <div className="grid grid-cols-3 gap-4">
          <div>
            <p className="text-sm text-gray-600">Peak Coverage Hour</p>
            <p className="text-2xl font-bold text-green-600">
              {analytics.peakHour.hour}:00 ({Math.round(analytics.peakHour.coverage)}%)
            </p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Lowest Coverage Hour</p>
            <p className="text-2xl font-bold text-red-600">
              {analytics.lowestHour.hour}:00 ({Math.round(analytics.lowestHour.coverage)}%)
            </p>
          </div>
          <div>
            <p className="text-sm text-gray-600">Hours with 100% Coverage</p>
            <p className="text-2xl font-bold text-gray-900">{analytics.fullCoverageHours}/24</p>
          </div>
        </div>
      </Card>

      {/* Team Performance Table */}
      <Card>
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Team Performance</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
              <tr>
                <th className="px-6 py-4 text-left text-xs font-bold text-gray-700 tracking-wider border-b border-gray-200">
                  User
                </th>
                <th className="px-6 py-4 text-right text-xs font-bold text-gray-700 tracking-wider border-b border-gray-200">
                  Total Count
                </th>
                <th className="px-6 py-4 text-right text-xs font-bold text-gray-700 tracking-wider border-b border-gray-200">
                  Total Portfolios Monitored
                </th>
                <th className="px-6 py-4 text-right text-xs font-bold text-gray-700 tracking-wider border-b border-gray-200">
                  Monitoring Active Hours
                </th>
                <th className="px-6 py-4 text-right text-xs font-bold text-gray-700 tracking-wider border-b border-gray-200">
                  Missed Alerts
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-100">
              {analytics.perUserStats.map((u, idx) => (
                <tr
                  key={u.user}
                  className={`transition-colors ${idx % 2 === 0 ? 'bg-white' : 'bg-gray-50/50'}`}
                  style={{ '--hover-color': 'rgba(118, 171, 63, 0.1)' } as any}
                  onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = 'rgba(118, 171, 63, 0.1)')}
                  onMouseLeave={(e) =>
                    (e.currentTarget.style.backgroundColor = idx % 2 === 0 ? 'white' : 'rgba(0, 0, 0, 0.02)')
                  }
                >
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div
                        className="flex-shrink-0 h-10 w-10 rounded-full flex items-center justify-center text-white font-bold text-sm shadow-sm"
                        style={{ backgroundColor: '#76AB3F' }}
                      >
                        {(u.displayName || u.user || '?').charAt(0).toUpperCase()}
                      </div>
                      <div className="ml-4">
                        <div className="text-sm font-bold text-gray-900">{u.displayName || u.user}</div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right">
                    <span className="text-sm font-semibold text-gray-900">{u.issues}</span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold bg-blue-100 text-blue-800">
                      {u.portfoliosCount}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold bg-purple-100 text-purple-800">
                      {u.hoursCount}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right">
                    <span
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold ${
                        (u.missedAlerts || 0) === 0
                          ? 'text-white'
                          : (u.missedAlerts || 0) <= 3
                          ? 'bg-yellow-100 text-yellow-800'
                          : 'bg-red-100 text-red-800'
                      }`}
                      style={(u.missedAlerts || 0) === 0 ? { backgroundColor: '#76AB3F' } : {}}
                    >
                      {u.missedAlerts || 0}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Card>

      {/* Leaderboards */}
      <div className="grid grid-cols-3 gap-6">
        <LeaderboardCard
          title="Most Total Portfolios Monitored"
          data={analytics.leaderboard.coverage}
          getValue={(u) => u.portfoliosCount}
        />
        <LeaderboardCard
          title="Most Active Issues"
          data={analytics.leaderboard.issuesYes}
          getValue={(u) => u.issuesYes}
        />
        <LeaderboardCard
          title="Most Monitoring Active Hours"
          data={analytics.leaderboard.hours}
          getValue={(u) => u.hoursCount}
        />
      </div>
    </div>
  )
}

interface MetricCardProps {
  label: string
  value: string | number
  accent?: string
  highlight?: string
}

const MetricCard: React.FC<MetricCardProps> = ({ label, value, accent, highlight }) => (
  <div className={`${highlight || 'bg-white'} rounded-lg p-4 shadow-sm border border-gray-200`}>
    <p className="text-sm text-gray-600 mb-1">{label}</p>
    <p className={`text-2xl font-bold ${accent || 'text-gray-900'}`}>{value}</p>
  </div>
)

interface LeaderboardCardProps {
  title: string
  data: Array<{
    user: string
    displayName: string
    issues: number
    issuesYes: number
    portfoliosCount: number
    hoursCount: number
    missedAlerts: number
  }>
  getValue: (user: any) => number
}

const LeaderboardCard: React.FC<LeaderboardCardProps> = ({ title, data, getValue }) => (
  <Card>
    <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
    <div className="space-y-2">
      {data.length === 0 ? (
        <p className="text-sm text-gray-500">No data available</p>
      ) : (
        data.map((user, idx) => (
          <div
            key={user.user}
            className="flex items-center justify-between p-3 rounded-lg hover:bg-gray-50 transition-colors cursor-pointer"
          >
            <div className="flex items-center gap-3">
              <div
                className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-bold text-xs ${
                  idx < 3 ? 'bg-gradient-to-br from-green-600 to-green-800' : 'bg-gray-400'
                }`}
              >
                {idx + 1}
              </div>
              <div>
                <p className="text-sm font-semibold text-gray-900">{user.displayName || user.user}</p>
              </div>
            </div>
            <span className="text-sm font-bold text-gray-900">{getValue(user)}</span>
          </div>
        ))
      )}
    </div>
  </Card>
)

export default PerformanceAnalytics
