import React, { useState, useEffect } from 'react'
import { useQuery } from '@tanstack/react-query'
import Button from '../common/Button'
import { useNavigate } from 'react-router-dom'
import { analyticsService } from '../../services/analyticsService'
import { adminService, PortfolioLock } from '../../services/adminService'
import { PortfolioActivity } from '../../types/analytics.types'
import PortfolioDetailModal from '../portfolio/PortfolioDetailModal'
import { useAuth } from '../../context/AuthContext'
import toast from 'react-hot-toast'

interface QuickPortfolioReferenceProps {
  onPortfolioSelected?: (portfolioId: string, hour: number) => void
}

const QuickPortfolioReference: React.FC<QuickPortfolioReferenceProps> = ({
  onPortfolioSelected,
}) => {
  const navigate = useNavigate()
  const { user } = useAuth()
  const [lastUpdated, setLastUpdated] = useState(new Date())
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedPortfolioId, setSelectedPortfolioId] = useState<string | null>(null)
  const [hoveredPortfolio, setHoveredPortfolio] = useState<string | null>(null)
  const [tooltipPosition, setTooltipPosition] = useState<{
    top: number
    left: number
    showAbove: boolean
  } | null>(null)

  // Use React Query to fetch portfolio activity
  const { data: portfolios = [], isLoading: loading, isRefetching: isRefreshing, error, refetch } = useQuery<PortfolioActivity[]>({
    queryKey: ['portfolio-activity'],
    queryFn: async () => {
      console.log('Fetching portfolio activity...')
      const data = await analyticsService.getPortfolioActivity()
      console.log('Portfolio activity fetched:', data)
      return data
    },
    refetchInterval: 15000, // Auto-refresh every 15 seconds
    staleTime: 0, // Always consider data stale to ensure fresh data
    gcTime: 0, // Don't cache to ensure we always get latest data (gcTime is the new name for cacheTime in v5)
  })

  // Fetch locked portfolios
  const { data: locks = [], error: locksError } = useQuery<PortfolioLock[]>({
    queryKey: ['portfolio-locks'],
    queryFn: adminService.getLocks,
    refetchInterval: 30000, // Refresh every 30 seconds to check for locks (reduced frequency)
    retry: 2, // Retry up to 2 times on failure
    retryDelay: 1000, // Wait 1 second between retries
    staleTime: 10000, // Consider data stale after 10 seconds
  })

  // Log errors but don't block the UI
  useEffect(() => {
    if (locksError) {
      console.error('Error fetching locks:', locksError)
      // Don't show toast for locks error as it's not critical for the UI
    }
  }, [locksError])

  // Create a set of locked portfolio IDs
  const lockedPortfolioIds = new Set(locks.map(lock => lock.portfolio_id))

  // Update lastUpdated when data changes
  useEffect(() => {
    if (portfolios && portfolios.length > 0) {
      setLastUpdated(new Date())
      console.log('Portfolios loaded:', portfolios.length, portfolios)
    } else if (!loading && portfolios.length === 0) {
      console.log('No portfolios found')
    }
  }, [portfolios, loading])

  // Handle errors
  useEffect(() => {
    if (error) {
      console.error('Failed to fetch portfolio activity:', error)
      const errorMessage = error instanceof Error 
        ? error.message 
        : (error as any)?.response?.data?.error || 'Failed to load portfolio activity'
      toast.error(errorMessage)
    }
  }, [error])

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'updated':
        return 'bg-green-100 border-green-300'
      case '1h':
        return 'bg-blue-100 border-blue-300'
      case '2h':
        return 'bg-yellow-200 border-yellow-400'
      case '3h':
        return 'bg-orange-200 border-orange-400'
      case 'no-activity':
        return 'bg-red-100 border-red-300'
      default:
        return 'bg-red-100 border-red-300'
    }
  }

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'updated':
        return 'Updated (<1h)'
      case '1h':
        return '1h Inactive'
      case '2h':
        return '2h Inactive'
      case '3h':
        return '3h Inactive'
      case 'no-activity':
        return 'No Activity (4h+)'
      default:
        return 'No Activity (4h+)'
    }
  }

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    })
  }

  const filteredPortfolios: PortfolioActivity[] = (portfolios || []).filter((p: PortfolioActivity) =>
    p.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    (p.subtitle || '').toLowerCase().includes(searchQuery.toLowerCase())
  )

  if (loading && portfolios.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="text-center py-8">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <p className="mt-2 text-gray-600">Loading portfolios...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      {/* Header Section */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-gray-900">Quick Portfolio Reference</h2>
          <div className="text-sm text-gray-700">
            Last updated (EST): <span className="font-semibold">{formatTime(lastUpdated)}</span> (Auto-refreshes every 15s for real-time sync)
          </div>
        </div>

        {/* Legend */}
        <div className="flex items-center gap-6 mb-4 pb-4 border-b border-gray-200 flex-wrap">
          <span className="text-sm font-medium text-gray-700">Legend:</span>
          {['updated', '1h', '2h', '3h', 'no-activity'].map((status) => {
            const colors = getStatusColor(status).split(' ')
            const bgColor = colors[0]
            const borderColor = colors[1] || colors[0]
            return (
              <div key={status} className="flex items-center gap-2">
                <div className={`w-4 h-4 rounded border-2 ${bgColor} ${borderColor}`}></div>
                <span className="text-xs text-gray-600">{getStatusLabel(status)}</span>
              </div>
            )
          })}
        </div>

        {/* Search and Log Issue */}
        <div className="flex items-center gap-4 mb-4">
          <div className="flex-1 relative">
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-4 py-2 pl-10 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <span className="absolute left-3 top-2.5 text-gray-400">üîç</span>
          </div>
          <Button 
            onClick={() => navigate('/issues?action=log')}
          >
            + Log New Issue
          </Button>
        </div>
      </div>

      {/* Portfolio Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 xl:grid-cols-7 gap-3">
        {filteredPortfolios.length === 0 ? (
          <div className="col-span-full text-center py-12">
            <p className="text-gray-500 text-lg">No portfolios found</p>
          </div>
        ) : (
          filteredPortfolios.map((portfolio) => {
            const isLocked = lockedPortfolioIds.has(portfolio.id)
            const statusColors = getStatusColor(portfolio.status)
            const colors = statusColors.split(' ')
            const bgColor = colors[0]
            const borderColor = colors[1] || colors[0]

            return (
              <div
                key={portfolio.id}
                className={`${bgColor} rounded-lg p-3 cursor-pointer transition-all hover:shadow-lg hover:scale-105 relative shadow-md ${
                  isLocked 
                    ? 'border-[6px] border-purple-600' 
                    : `border-2 ${borderColor}`
                }`}
                onClick={() => {
                  setSelectedPortfolioId(portfolio.id)
                  // Don't open sidebar directly - open modal first
                }}
                onMouseEnter={(e) => {
                  const rect = e.currentTarget.getBoundingClientRect()
                  const viewportHeight = window.innerHeight
                  const spaceAbove = rect.top
                  const spaceBelow = viewportHeight - rect.bottom

                  // Show tooltip above if there's more space above, otherwise below
                  const showAbove = spaceAbove > spaceBelow

                  setHoveredPortfolio(portfolio.id)
                  setTooltipPosition({
                    top: showAbove ? rect.top - 10 : rect.bottom + 10,
                    left: rect.left + rect.width / 2,
                    showAbove: showAbove
                  })
                }}
                onMouseLeave={() => {
                  setHoveredPortfolio(null)
                  setTooltipPosition(null)
                }}
              >
                {/* Y/H Badge - Top Right */}
                <div className="absolute top-2 right-2 bg-blue-500 text-white px-2 py-1 rounded-md flex items-center gap-1 shadow-sm">
                  <span className="text-xs font-bold">
                    {portfolio.yValue.startsWith('H') ? 'H' : 'Y'}
                  </span>
                  <span className="text-sm font-bold">
                    {portfolio.yValue.replace(/^[YH]/, '')}
                  </span>
                </div>

                {/* Portfolio Name with Site Range */}
                <div className="pr-16 pt-1">
                  <h3 className="font-bold text-gray-900 text-base leading-tight">
                    {portfolio.name}
                    {portfolio.siteRange && (
                      <span className="text-gray-600 font-normal text-sm"> ({portfolio.siteRange})</span>
                    )}
                  </h3>
                </div>

                {/* Subtitle */}
                {portfolio.subtitle && (
                  <p className="text-xs text-gray-700 font-medium mt-1">{portfolio.subtitle}</p>
                )}
              </div>
            )
          })
        )}
      </div>

      {/* Hover Tooltip */}
      {hoveredPortfolio && tooltipPosition && (() => {
        const portfolio = portfolios.find(p => p.id === hoveredPortfolio)
        if (!portfolio) return null
        const isLockedTooltip = lockedPortfolioIds.has(portfolio.id)
        const lockInfo = locks.find(l => l.portfolio_id === portfolio.id)

        return (
          <div
            className="fixed z-[9999] bg-gray-900 text-white text-xs rounded-lg shadow-xl p-3 pointer-events-none"
            style={{
              top: `${tooltipPosition.top}px`,
              left: `${tooltipPosition.left}px`,
              transform: tooltipPosition.showAbove
                ? 'translate(-50%, -100%)'
                : 'translate(-50%, 0)',
            }}
          >
            <div className="font-bold text-sm mb-2 pb-2 border-b border-gray-700">
              {portfolio.name}
              {portfolio.siteRange && ` (${portfolio.siteRange})`}
            </div>
            <div className="space-y-1">
              <div className="text-gray-200">
                <span className="text-gray-400">Status:</span> {getStatusLabel(portfolio.status)}
              </div>
              <div className="text-gray-200">
                <span className="text-gray-400">Y/H Value:</span> {portfolio.yValue}
              </div>
              {portfolio.lastUpdated && (
                <div className="text-gray-200">
                  <span className="text-gray-400">Last Updated:</span>{' '}
                  {new Date(portfolio.lastUpdated).toLocaleString()}
                </div>
              )}
              {isLockedTooltip && lockInfo && (
                <div className="text-purple-300 mt-2 pt-2 border-t border-gray-700">
                  üîí Locked by {lockInfo.monitored_by?.split('@')[0] || 'Unknown'}
                  <div className="text-xs text-gray-400 mt-1">
                    Hour {lockInfo.issue_hour}:00
                  </div>
                </div>
              )}
            </div>
            <div className="mt-2 pt-2 border-t border-gray-700 text-xs text-gray-400">
              Click for options
            </div>
            {/* Arrow pointing to card */}
            <div
              className={`absolute left-1/2 transform -translate-x-1/2 border-4 border-transparent ${
                tooltipPosition.showAbove
                  ? 'top-full -mt-1 border-t-gray-900'
                  : 'bottom-full mb-1 border-b-gray-900'
              }`}
            ></div>
          </div>
        )
      })()}

      {/* Portfolio Detail Modal */}
      {selectedPortfolioId && (
        <PortfolioDetailModal
          isOpen={!!selectedPortfolioId}
          onClose={() => setSelectedPortfolioId(null)}
          portfolioId={selectedPortfolioId}
          onLogIssue={(portfolioId, hour) => {
            setSelectedPortfolioId(null) // Close modal
            if (onPortfolioSelected) {
              onPortfolioSelected(portfolioId, hour)
            }
          }}
        />
      )}
    </div>
  )
}

export default QuickPortfolioReference
