import React, { useState } from 'react'
import { useNavigate, Link } from 'react-router-dom'
import { useAuth } from '../../context/AuthContext'
import toast from 'react-hot-toast'

const UserLogin: React.FC = () => {
  const navigate = useNavigate()
  const { login } = useAuth()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [loading, setLoading] = useState(false)

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    try {
      // Use username as email for user login
      await login({ email: username, password })
      toast.success('Login successful!')
      navigate('/')
    } catch (error: any) {
      const errorMessage = error.response?.data?.error || error.message || 'Login failed'
      toast.error(errorMessage)
      console.error('Login error details:', error)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="w-full max-w-5xl bg-white rounded-lg shadow-lg overflow-hidden">
        <div className="flex flex-col md:flex-row min-h-[600px]">
          {/* Left Section - White Background */}
          <div className="w-full md:w-1/2 bg-white flex flex-col justify-center items-center p-8 md:p-12">
            {/* Logo */}
            <div className="mb-8">
              <img 
                src="/login.png" 
                alt="American Green Solutions Logo" 
                className="max-w-full h-auto"
                style={{ maxHeight: '300px' }}
              />
            </div>

            {/* Company Name */}
            <h1 className="text-5xl font-bold text-black mb-2" style={{ fontFamily: 'serif' }}>
              AMERICAN
            </h1>
            <h2 className="text-2xl font-semibold text-black mb-8" style={{ fontFamily: 'serif' }}>
              GREEN SOLUTIONS
            </h2>

            {/* Welcome Text */}
            <h3 className="text-4xl font-bold text-black mb-2">
              Welcome
            </h3>
            <p className="text-lg text-black">
              User access
            </p>
          </div>

          {/* Right Section - Green Background */}
          <div className="w-full md:w-1/2 flex flex-col justify-center p-8 md:p-12" style={{ backgroundColor: '#b1d48b' }}>
            <h2 className="text-4xl font-bold text-white mb-2">
              User Login
            </h2>
            <p className="text-lg text-white mb-8">
              Secure access to your account
            </p>

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Username */}
              <div>
                <label htmlFor="username" className="block text-white font-medium mb-2">
                  Username
                </label>
                <div className="relative">
                  <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400">
                    ðŸ‘¤
                  </span>
                  <input
                    id="username"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    className="w-full pl-12 pr-4 py-3 bg-white rounded-lg text-gray-900 placeholder-gray-400 focus:outline-none"
                    style={{ 
                      boxShadow: '0 0 0 0px transparent',
                    }}
                    onFocus={(e) => {
                      e.target.style.boxShadow = '0 0 0 2px #b1d48b'
                    }}
                    onBlur={(e) => {
                      e.target.style.boxShadow = '0 0 0 0px transparent'
                    }}
                    placeholder="Type username"
                  />
                </div>
              </div>

              {/* Password */}
              <div>
                <label htmlFor="password" className="block text-white font-medium mb-2">
                  Password
                </label>
                <div className="relative">
                  <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400">
                    ðŸ”’
                  </span>
                  <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    className="w-full pl-12 pr-4 py-3 bg-white rounded-lg text-gray-900 placeholder-gray-400 focus:outline-none"
                    style={{ 
                      boxShadow: '0 0 0 0px transparent',
                    }}
                    onFocus={(e) => {
                      e.target.style.boxShadow = '0 0 0 2px #b1d48b'
                    }}
                    onBlur={(e) => {
                      e.target.style.boxShadow = '0 0 0 0px transparent'
                    }}
                    placeholder="Type password"
                  />
                </div>
                <div className="mt-2 text-right">
                  <Link to="/forgot-password" className="text-white hover:opacity-80 text-sm font-medium transition-opacity">
                    Forgot password?
                  </Link>
                </div>
              </div>

              {/* Login Button */}
              <button
                type="submit"
                disabled={loading}
                className="w-full text-white py-4 rounded-lg font-semibold text-lg flex items-center justify-center gap-2 transition-opacity disabled:opacity-50 disabled:cursor-not-allowed hover:opacity-90"
                style={{ backgroundColor: '#9bc077' }}
              >
                <span>ðŸ‘¤</span>
                <span>{loading ? 'Signing in...' : 'User Login'}</span>
                <span>â†’</span>
              </button>
            </form>

            {/* Divider and Admin Link */}
            <div className="mt-8 pt-6 border-t" style={{ borderColor: 'rgba(255, 255, 255, 0.3)' }}>
              <Link
                to="/admin/login"
                className="block w-full text-center text-white hover:opacity-80 font-medium transition-opacity"
              >
                Admin user? Click here
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default UserLogin
