import React, { useState, useEffect } from 'react'
import { useQuery } from '@tanstack/react-query'
import Button from '../common/Button'
import { useNavigate } from 'react-router-dom'
import { analyticsService } from '../../services/analyticsService'
import { PortfolioActivity } from '../../types/analytics.types'
import toast from 'react-hot-toast'

const QuickPortfolioReference: React.FC = () => {
  const navigate = useNavigate()
  const [lastUpdated, setLastUpdated] = useState(new Date())
  const [searchQuery, setSearchQuery] = useState('')

  // Use React Query to fetch portfolio activity
  const { data: portfolios = [], isLoading: loading, isRefetching: isRefreshing, error } = useQuery<PortfolioActivity[]>({
    queryKey: ['portfolio-activity'],
    queryFn: analyticsService.getPortfolioActivity,
    refetchInterval: 15000, // Auto-refresh every 15 seconds
  })

  // Update lastUpdated when data changes
  useEffect(() => {
    if (portfolios && portfolios.length > 0) {
      setLastUpdated(new Date())
    }
  }, [portfolios])

  // Handle errors
  useEffect(() => {
    if (error) {
      console.error('Failed to fetch portfolio activity:', error)
      toast.error('Failed to load portfolio activity')
    }
  }, [error])

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'no-activity':
        return 'bg-red-500'
      case '3h':
        return 'bg-orange-500'
      case '2h':
        return 'bg-yellow-500'
      case '1h':
        return 'bg-blue-400'
      case 'updated':
        return ''
      default:
        return 'bg-gray-300'
    }
  }

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'no-activity':
        return 'No Activity (4h+)'
      case '3h':
        return '3h'
      case '2h':
        return '2h'
      case '1h':
        return '1h'
      case 'updated':
        return 'Updated (<1h)'
      default:
        return ''
    }
  }

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit', 
      second: '2-digit' 
    })
  }

  const filteredPortfolios = portfolios.filter(p =>
    p.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    p.subtitle.toLowerCase().includes(searchQuery.toLowerCase())
  )

  if (loading && portfolios.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="text-center py-8">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <p className="mt-2 text-gray-600">Loading portfolios...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      {/* Header Section */}
      <div className="mb-4">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-bold text-gray-900">Quick Portfolio Reference</h2>
          <div className="text-sm text-gray-700">
            Last updated (EST): <span className="font-semibold">{formatTime(lastUpdated)}</span> (Auto-refreshes every 15s for real-time sync)
          </div>
        </div>

        {/* Legend */}
        <div className="flex items-center gap-6 mb-4 pb-4 border-b border-gray-200">
          <span className="text-sm font-medium text-gray-700">Legend:</span>
          {['no-activity', '3h', '2h', '1h', 'updated'].map((status) => (
            <div key={status} className="flex items-center gap-2">
              <div className={`w-4 h-4 rounded-full ${getStatusColor(status)}`}></div>
              <span className="text-xs text-gray-600">{getStatusLabel(status)}</span>
            </div>
          ))}
        </div>

        {/* Search and Log Issue */}
        <div className="flex items-center gap-4 mb-4">
          <div className="flex-1 relative">
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-4 py-2 pl-10 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <span className="absolute left-3 top-2.5 text-gray-400">üîç</span>
          </div>
          <Button 
            onClick={() => navigate('/issues?action=log')}
          >
            + Log New Issue
          </Button>
        </div>
      </div>

      {/* Portfolio Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {filteredPortfolios.length === 0 ? (
          <div className="col-span-full text-center py-12">
            <p className="text-gray-500 text-lg">No portfolios found</p>
          </div>
        ) : (
          filteredPortfolios.map((portfolio) => (
            <div
              key={portfolio.id}
              className={`border-2 rounded-lg p-5 cursor-pointer transition-all hover:shadow-xl hover:scale-105 ${
                portfolio.status === 'no-activity' 
                  ? 'bg-red-50 border-red-400 shadow-md' 
                  : 'bg-white border-gray-300 shadow-sm'
              }`}
              onClick={() => navigate(`/portfolios/${portfolio.id}`)}
            >
              {/* Portfolio Header */}
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <h3 className="font-bold text-gray-900 text-xl mb-1">{portfolio.name}</h3>
                  {portfolio.siteRange && (
                    <p className="text-sm text-gray-600">({portfolio.siteRange})</p>
                  )}
                </div>
                <div 
                  className={`w-4 h-4 rounded-full shadow-sm ${getStatusColor(portfolio.status)}`}
                  style={portfolio.status === 'updated' ? { backgroundColor: '#76ab3f' } : {}}
                ></div>
              </div>

              {/* Subtitle */}
              <p className="text-base text-gray-700 font-medium mb-3">{portfolio.subtitle}</p>

              {/* Y Value Section */}
              <div className="flex items-center justify-between mt-4 pt-4 border-t-2 border-gray-300">
                <span className="text-sm font-semibold text-gray-600">Y</span>
                <span className="text-2xl font-bold text-gray-900">{portfolio.yValue}</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}

export default QuickPortfolioReference
