    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['personnel'] })
      toast.success('Personnel updated successfully')
      setShowModal(false)
      setFormData({ name: '', role: 'monitor' })
      setEditingPersonnel(null)
