import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import Modal from '../common/Modal'
import Button from '../common/Button'
import Input from '../common/Input'
import { portfolioService } from '../../services/portfolioService'
import { Portfolio } from '../../types/portfolio.types'
import toast from 'react-hot-toast'

interface PortfolioDetailModalProps {
  isOpen: boolean
  onClose: () => void
  portfolioId: string
}

const PortfolioDetailModal: React.FC<PortfolioDetailModalProps> = ({
  isOpen,
  onClose,
  portfolioId,
}) => {
  const navigate = useNavigate()
  const queryClient = useQueryClient()
  const [allSitesChecked, setAllSitesChecked] = useState<'Yes' | 'No' | null>(null)
  const [sitesCheckedDetails, setSitesCheckedDetails] = useState('')

  // Fetch portfolio details
  const { data: portfolio, isLoading } = useQuery<Portfolio>({
    queryKey: ['portfolio', portfolioId],
    queryFn: () => portfolioService.getById(portfolioId),
    enabled: isOpen && !!portfolioId,
  })

  // Update all sites checked status
  const updateMutation = useMutation({
    mutationFn: (data: { allSitesChecked: 'Yes' | 'No'; sitesCheckedDetails?: string }) => {
      return portfolioService.updateAllSitesChecked(portfolioId, {
        allSitesChecked: data.allSitesChecked,
        hour: new Date().getHours(),
        date: new Date().toISOString().split('T')[0],
        checkedBy: 'Current User', // TODO: Get from auth context
        notes: data.sitesCheckedDetails,
      })
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['portfolio', portfolioId] })
      queryClient.invalidateQueries({ queryKey: ['portfolios'] })
      queryClient.invalidateQueries({ queryKey: ['portfolio-activity'] })
      toast.success('Portfolio status updated successfully')
      setAllSitesChecked(null)
      setSitesCheckedDetails('')
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.error || 'Failed to update portfolio status')
    },
  })

  // Reset form when modal opens/closes or portfolio changes
  useEffect(() => {
    if (isOpen && portfolio) {
      setAllSitesChecked(portfolio.all_sites_checked || null)
      setSitesCheckedDetails(portfolio.sites_checked_details || '')
    } else {
      setAllSitesChecked(null)
      setSitesCheckedDetails('')
    }
  }, [isOpen, portfolio])

  const handleAllSitesChecked = (value: 'Yes' | 'No') => {
    setAllSitesChecked(value)
    if (value === 'Yes' || value === 'No') {
      updateMutation.mutate({
        allSitesChecked: value,
        sitesCheckedDetails: sitesCheckedDetails || undefined,
      })
    }
  }

  const handleViewIssues = () => {
    onClose()
    navigate(`/issues?portfolio=${portfolioId}`)
  }

  const handleLogNewIssue = () => {
    onClose()
    navigate(`/issues?action=log&portfolio=${portfolioId}`)
  }

  if (isLoading) {
    return (
      <Modal isOpen={isOpen} onClose={onClose} title="Loading...">
        <div className="text-center py-8">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      </Modal>
    )
  }

  if (!portfolio) return null

  const displayName = portfolio.site_range
    ? `${portfolio.name} (${portfolio.site_range})`
    : portfolio.name

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={displayName} size="lg">
      <div className="space-y-6">
        {/* All Sites Checked Question */}
        <div>
          <label className="block text-sm font-semibold text-gray-900 mb-2">
            All sites checked?
          </label>
          <p className="text-sm text-gray-600 mb-4">
            Confirm after you verify every site belonging to this portfolio.
          </p>
          <div className="flex gap-3">
            <button
              onClick={() => handleAllSitesChecked('Yes')}
              className={`flex-1 px-4 py-3 rounded-lg font-medium transition-colors ${
                allSitesChecked === 'Yes'
                  ? 'bg-green-500 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              Yes
            </button>
            <button
              onClick={() => handleAllSitesChecked('No')}
              className={`flex-1 px-4 py-3 rounded-lg font-medium transition-colors ${
                allSitesChecked === 'No'
                  ? 'bg-yellow-500 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              No
            </button>
          </div>
        </div>

        {/* Sites Checked Details */}
        <div>
          <label className="block text-sm font-semibold text-gray-900 mb-2">
            Which sites have you checked? (e.g., "Site 1 to Site 5", "Sites 1-10")
          </label>
          <Input
            type="text"
            value={sitesCheckedDetails}
            onChange={(e) => setSitesCheckedDetails(e.target.value)}
            placeholder="Enter sites checked (e.g., Site 1 to Site 5)"
            className="w-full"
          />
          {sitesCheckedDetails && (
            <button
              onClick={() => {
                if (allSitesChecked) {
                  updateMutation.mutate({
                    allSitesChecked,
                    sitesCheckedDetails,
                  })
                }
              }}
              className="mt-2 text-sm text-blue-600 hover:text-blue-700"
            >
              Update sites checked details
            </button>
          )}
        </div>

        {/* Action Buttons */}
        <div className="space-y-3 pt-4 border-t border-gray-200">
          <p className="text-sm font-semibold text-gray-900 mb-3">What would you like to do?</p>

          {/* View Issues Button */}
          <button
            onClick={handleViewIssues}
            className="w-full flex items-center justify-between px-6 py-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors shadow-sm"
          >
            <div className="flex items-center gap-3">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
              </svg>
              <div className="text-left">
                <div className="font-semibold">View Issues</div>
                <div className="text-sm text-blue-100">See all logged issues for this portfolio</div>
              </div>
            </div>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>

          {/* Log New Issue Button */}
          <button
            onClick={handleLogNewIssue}
            className="w-full flex items-center justify-between px-6 py-4 rounded-lg transition-colors shadow-sm"
            style={{ backgroundColor: '#76ab3f', color: 'white' }}
            onMouseEnter={(e) => e.currentTarget.style.opacity = '0.9'}
            onMouseLeave={(e) => e.currentTarget.style.opacity = '1'}
          >
            <div className="flex items-center gap-3">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
              <div className="text-left">
                <div className="font-semibold">Log New Issue</div>
                <div className="text-sm" style={{ color: 'rgba(255, 255, 255, 0.9)' }}>
                  Report a new issue for this portfolio
                </div>
              </div>
            </div>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      </div>
    </Modal>
  )
}

export default PortfolioDetailModal
