import React, { useState, useEffect } from 'react'
import Card from '../common/Card'
import { Bar } from 'react-chartjs-2'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js'
import { analyticsService } from '../../services/analyticsService'
import { HourlyCoverage } from '../../types/analytics.types'
import toast from 'react-hot-toast'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend)

const HourlyCoverageAnalysis: React.FC = () => {
  const [startDate, setStartDate] = useState('')
  const [endDate, setEndDate] = useState('')
  const [selectedRange, setSelectedRange] = useState<'today' | 'week' | 'month'>('today')
  const [coverageData, setCoverageData] = useState<HourlyCoverage[]>([])
  const [loading, setLoading] = useState(true)

  const fetchHourlyCoverage = async (start?: string, end?: string) => {
    try {
      setLoading(true)
      const data = await analyticsService.getHourlyCoverageWithDateRange(start, end)
      setCoverageData(data)
    } catch (error: any) {
      console.error('Failed to fetch hourly coverage:', error)
      toast.error('Failed to load hourly coverage data')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    // Initialize with today's date
    const today = new Date().toISOString().split('T')[0]
    setStartDate(today)
    setEndDate(today)
    fetchHourlyCoverage(today, today)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  useEffect(() => {
    if (startDate && endDate && !loading) {
      fetchHourlyCoverage(startDate, endDate)
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [startDate, endDate])

  const chartData = {
    labels: Array.from({ length: 24 }, (_, i) => `${i}`),
    datasets: [
      {
        label: 'Portfolio Risk Distribution',
        data: coverageData.map(item => item.count),
        backgroundColor: 'rgba(59, 130, 246, 0.6)',
        borderColor: 'rgba(59, 130, 246, 1)',
        borderWidth: 1,
      },
    ],
  }

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      title: {
        display: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
    },
  }

  const handleRangeClick = (range: 'today' | 'week' | 'month') => {
    setSelectedRange(range)
    const today = new Date()
    if (range === 'today') {
      const todayStr = today.toISOString().split('T')[0]
      setStartDate(todayStr)
      setEndDate(todayStr)
      fetchHourlyCoverage(todayStr, todayStr)
    } else if (range === 'week') {
      const weekAgo = new Date(today)
      weekAgo.setDate(today.getDate() - 7)
      const weekAgoStr = weekAgo.toISOString().split('T')[0]
      const todayStr = today.toISOString().split('T')[0]
      setStartDate(weekAgoStr)
      setEndDate(todayStr)
      fetchHourlyCoverage(weekAgoStr, todayStr)
    } else if (range === 'month') {
      const monthAgo = new Date(today)
      monthAgo.setMonth(today.getMonth() - 1)
      const monthAgoStr = monthAgo.toISOString().split('T')[0]
      const todayStr = today.toISOString().split('T')[0]
      setStartDate(monthAgoStr)
      setEndDate(todayStr)
      fetchHourlyCoverage(monthAgoStr, todayStr)
    }
  }

  return (
    <Card className="mb-6">
      {/* Header */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Hourly Coverage Analysis</h2>
        <p className="text-gray-600 text-sm">
          Portfolio risk distribution analysis based on temporal coverage theory.
        </p>
      </div>

      {/* Date Controls */}
      <div className="mb-6 space-y-4">
        <div className="flex items-center gap-4 flex-wrap">
          <div className="flex items-center gap-2">
            <label className="text-sm font-medium text-gray-700">Start Date:</label>
            <input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex items-center gap-2">
            <label className="text-sm font-medium text-gray-700">End Date:</label>
            <input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex items-center gap-2">
            <button
              onClick={() => handleRangeClick('today')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'today'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'today' ? { backgroundColor: '#76ab3f' } : {}}
            >
              Today
            </button>
            <button
              onClick={() => handleRangeClick('week')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'week'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'week' ? { backgroundColor: '#7ac83f' } : {}}
            >
              Week
            </button>
            <button
              onClick={() => handleRangeClick('month')}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                selectedRange === 'month'
                  ? 'text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              style={selectedRange === 'month' ? { backgroundColor: '#7ac83f' } : {}}
            >
              Month
            </button>
          </div>
        </div>
        <p className="text-sm text-gray-600">
          Showing data for: {startDate || new Date().toISOString().split('T')[0]}
        </p>
      </div>

      {/* Chart Section */}
      <div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">
          Portfolio Risk Distribution by Hour
        </h3>
        <p className="text-sm text-gray-600 mb-4">
          Visual representation of systemic risk concentration across the 24-hour cycle.
        </p>
        <div className="bg-gray-50 border border-gray-200 rounded-lg p-6" style={{ height: '400px' }}>
          {loading ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <div className="inline-block animate-spin rounded-full h-10 w-10 border-b-2 border-blue-600"></div>
                <p className="mt-3 text-gray-600 font-medium">Loading chart data...</p>
              </div>
            </div>
          ) : (
            <Bar data={chartData} options={chartOptions} />
          )}
        </div>
      </div>
    </Card>
  )
}

export default HourlyCoverageAnalysis
