import React, { useState } from 'react'
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom'
import { useAuth } from '../../context/AuthContext'
import toast from 'react-hot-toast'

const MainLayout: React.FC = () => {
  const { user, logout } = useAuth()
  const location = useLocation()
  const navigate = useNavigate()
  const [sidebarOpen, setSidebarOpen] = useState(true)

  const handleLogout = async () => {
    try {
      await logout()
      navigate('/login')
      toast.success('Logged out successfully')
    } catch (error) {
      toast.error('Failed to logout')
    }
  }

  const getCurrentHourEST = () => {
    const now = new Date()
    const estOffset = -5 // EST is UTC-5
    const utc = now.getTime() + (now.getTimezoneOffset() * 60000)
    const estTime = new Date(utc + (estOffset * 3600000))
    return estTime.getHours()
  }

  const navItems = [
    { path: '/', label: 'Dashboard & Log Issues', icon: 'üìä' },
    { path: '/issues', label: 'Issue Details', icon: 'üé´' },
    { path: '/analytics', label: 'Performance Analytics', icon: 'üìà' },
    { path: '/issues-by-user', label: 'Issues by User', icon: 'üë•' },
    { path: '/coverage-matrix', label: 'Coverage Matrix', icon: 'üìä' },
  ]

  if (user?.role === 'tenant_admin' || user?.role === 'super_admin') {
    navItems.push({ path: '/admin', label: 'Admin Panel', icon: '‚öôÔ∏è' })
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Top Header */}
      <header className="bg-white border-b-2 shadow-sm" style={{ borderColor: '#7ac83f' }}>
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 rounded-lg flex items-center justify-center shadow-md" style={{ backgroundColor: '#7ac83f' }}>
                <span className="text-white text-2xl">üè†</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900" style={{ fontFamily: 'serif' }}>
                  AMERICAN GREEN SOLUTIONS
                </h1>
              </div>
            </div>

            {/* Center Title */}
            <div className="flex-1 text-center">
              <h1 className="text-4xl font-bold text-gray-900" style={{ fontFamily: 'serif' }}>
                {user?.tenantName || 'Standard Solar'}
              </h1>
              <p className="text-xl text-gray-700 font-semibold">Issue Tracker</p>
            </div>

            {/* Right Side */}
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <div className="w-10 h-10 rounded-full flex items-center justify-center border-2" style={{ backgroundColor: '#f0f9f0', borderColor: '#7ac83f' }}>
                  <span className="font-bold text-lg" style={{ color: '#7ac83f' }}>
                    {(user?.fullName || user?.email || 'A')[0].toUpperCase()}
                  </span>
                </div>
                <div className="text-left">
                  <p className="text-sm font-semibold text-gray-900">{user?.fullName || 'Admin'}</p>
                  <p className="text-xs text-gray-600 lowercase">{user?.role || 'administrator'}</p>
                </div>
              </div>
              {(user?.role === 'tenant_admin' || user?.role === 'super_admin') && (
                <Link
                  to="/admin"
                  className="px-4 py-2 text-white rounded-lg transition-colors font-medium shadow-sm hover:opacity-90"
                  style={{ backgroundColor: '#7ac83f' }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#6ba836'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#7ac83f'}
                >
                  Admin Panel
                </Link>
              )}
              <button
                onClick={handleLogout}
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors font-medium shadow-sm"
              >
                Logout
              </button>
              <div className="px-4 py-2 text-white rounded-lg font-semibold" style={{ backgroundColor: '#7ac83f' }}>
                Current Hour (EST) {getCurrentHourEST()}
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Bar */}
        <nav className="px-6" style={{ backgroundColor: '#7ac83f' }}>
          <div className="flex items-center gap-1">
            {navItems.map((item) => {
              const isActive = location.pathname === item.path || 
                (item.path === '/' && location.pathname === '/')
              return (
                <Link
                  key={item.path}
                  to={item.path}
                  className="px-6 py-3 text-white font-medium transition-colors relative hover:opacity-80"
                  style={{ 
                    backgroundColor: isActive ? '#6ba836' : 'transparent'
                  }}
                >
                  {item.label}
                  {isActive && (
                    <div className="absolute bottom-0 left-0 right-0 h-1 bg-white"></div>
                  )}
                </Link>
              )
            })}
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="px-6 py-6">
        <Outlet />
      </main>
    </div>
  )
}

export default MainLayout
