import React, { useState, useMemo } from 'react'
import { useQuery } from '@tanstack/react-query'
import { Bar } from 'react-chartjs-2'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js'
import { issueService } from '../../services/issueService'
import { portfolioService } from '../../services/portfolioService'
import { analyticsService } from '../../services/analyticsService'
import { Issue } from '../../types/issue.types'
import { Portfolio } from '../../types/portfolio.types'
import { HourlyCoverage } from '../../types/analytics.types'
import Card from '../common/Card'
import Button from '../common/Button'
import toast from 'react-hot-toast'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement)

interface PerformanceAnalyticsProps {
  dateRange?: 'today' | 'yesterday' | 'week' | 'custom'
  startDate?: string
  endDate?: string
}

const PerformanceAnalytics: React.FC<PerformanceAnalyticsProps> = ({
  dateRange = 'today',
  startDate,
  endDate,
}) => {
  const [selectedRange, setSelectedRange] = useState<'today' | 'yesterday' | 'week' | 'custom'>(
    dateRange
  )
  const [customStartDate, setCustomStartDate] = useState(startDate || '')
  const [customEndDate, setCustomEndDate] = useState(endDate || '')

  // Fetch portfolios
  const { data: portfolios = [] } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  // Fetch issues
  const { data: issues = [] } = useQuery<Issue[]>({
    queryKey: ['issues'],
    queryFn: () => issueService.getAll({}),
  })

  // Fetch hourly coverage
  const { data: hourlyCoverage = [] } = useQuery<HourlyCoverage[]>({
    queryKey: ['hourly-coverage-range', selectedRange, customStartDate, customEndDate],
    queryFn: async () => {
      const today = new Date()
      let start: string | undefined
      let end: string | undefined

      if (selectedRange === 'today') {
        const todayStr = today.toISOString().split('T')[0]
        start = todayStr
        end = todayStr
      } else if (selectedRange === 'yesterday') {
        const yesterday = new Date(today)
        yesterday.setDate(yesterday.getDate() - 1)
        const yesterdayStr = yesterday.toISOString().split('T')[0]
        start = yesterdayStr
        end = yesterdayStr
      } else if (selectedRange === 'week') {
        const weekAgo = new Date(today)
        weekAgo.setDate(today.getDate() - 7)
        start = weekAgo.toISOString().split('T')[0]
        end = today.toISOString().split('T')[0]
      } else if (selectedRange === 'custom' && customStartDate && customEndDate) {
        start = customStartDate
        end = customEndDate
      } else {
        const todayStr = today.toISOString().split('T')[0]
        start = todayStr
        end = todayStr
      }

      return analyticsService.getHourlyCoverageWithDateRange(start, end)
    },
  })

  // Calculate analytics
  const analytics = useMemo(() => {
    const today = new Date()
    today.setHours(0, 0, 0, 0)

    // Filter issues based on date range
    let filteredIssues = issues

    if (selectedRange === 'today') {
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        issueDate.setHours(0, 0, 0, 0)
        return issueDate.getTime() === today.getTime()
      })
    } else if (selectedRange === 'yesterday') {
      const yesterday = new Date(today)
      yesterday.setDate(yesterday.getDate() - 1)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        issueDate.setHours(0, 0, 0, 0)
        return issueDate.getTime() === yesterday.getTime()
      })
    } else if (selectedRange === 'week') {
      const weekAgo = new Date(today)
      weekAgo.setDate(today.getDate() - 7)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        return issueDate >= weekAgo
      })
    } else if (selectedRange === 'custom' && customStartDate && customEndDate) {
      const start = new Date(customStartDate)
      const end = new Date(customEndDate)
      end.setHours(23, 59, 59, 999)
      filteredIssues = issues.filter((issue) => {
        const issueDate = new Date(issue.created_at)
        return issueDate >= start && issueDate <= end
      })
    }

    const totalPortfolios = portfolios.length || 26
    const uniquePortfoliosToday = new Set(filteredIssues.map((i) => i.portfolio_id))
    const overallCoverage = totalPortfolios > 0 ? (uniquePortfoliosToday.size / totalPortfolios) * 100 : 0

    // Calculate active hours (hours with at least one issue)
    const activeHoursSet = new Set(filteredIssues.map((i) => i.issue_hour))
    const activeHours = activeHoursSet.size

    // Calculate average hourly coverage
    const avgHourlyCoverage =
      hourlyCoverage.length > 0
        ? hourlyCoverage.reduce((sum, h) => sum + h.coverage, 0) / hourlyCoverage.length
        : 0
    const performanceScore = Math.round(avgHourlyCoverage / 10)

    // Find peak and lowest coverage hours
    const peakHour = hourlyCoverage.reduce(
      (max, current) => (current.coverage > max.coverage ? current : max),
      hourlyCoverage[0] || { hour: 0, coverage: 0 }
    )
    const lowestHour = hourlyCoverage.reduce(
      (min, current) => (current.coverage < min.coverage ? current : min),
      hourlyCoverage[0] || { hour: 0, coverage: 0 }
    )

    // Calculate hours with 100% coverage
    const fullCoverageHours = hourlyCoverage.filter((h) => h.coverage === 100).length

    // Calculate per-user stats
    const userStatsMap: {
      [key: string]: {
        user: string
        displayName: string
        issues: number
        issuesYes: number
        portfoliosCount: number
        hoursCount: number
        missedAlerts: number
      }
    } = {}

    filteredIssues.forEach((issue) => {
      const monitoredBy = issue.monitored_by?.[0] || 'Unknown'
      const username = monitoredBy.split('@')[0]
      const displayName = username || 'Unknown'

      if (!userStatsMap[monitoredBy]) {
        userStatsMap[monitoredBy] = {
          user: monitoredBy,
          displayName,
          issues: 0,
          issuesYes: 0,
          portfoliosCount: 0,
          hoursCount: 0,
          missedAlerts: 0,
        }
      }

      userStatsMap[monitoredBy].issues++
      if (issue.description && issue.description.toLowerCase() !== 'no issue') {
        userStatsMap[monitoredBy].issuesYes++
      }
      if (issue.missed_by && issue.missed_by.length > 0) {
        userStatsMap[monitoredBy].missedAlerts += issue.missed_by.length
      }
    })

    // Calculate unique portfolios and hours per user
    const userPortfoliosMap: { [key: string]: Set<string> } = {}
    const userHoursMap: { [key: string]: Set<number> } = {}

    filteredIssues.forEach((issue) => {
      const monitoredBy = issue.monitored_by?.[0] || 'Unknown'
      if (!userPortfoliosMap[monitoredBy]) {
        userPortfoliosMap[monitoredBy] = new Set()
        userHoursMap[monitoredBy] = new Set()
      }
      userPortfoliosMap[monitoredBy].add(issue.portfolio_id)
      userHoursMap[monitoredBy].add(issue.issue_hour)
    })

    Object.keys(userStatsMap).forEach((user) => {
      userStatsMap[user].portfoliosCount = userPortfoliosMap[user]?.size || 0
      userStatsMap[user].hoursCount = userHoursMap[user]?.size || 0
    })

    const perUserStats = Object.values(userStatsMap)

    // Leaderboards
    const byIssuesYes = [...perUserStats].sort(
      (a, b) => b.issuesYes - a.issuesYes || b.issues - a.issues
    )
    const byCoverage = [...perUserStats].sort(
      (a, b) => b.portfoliosCount - a.portfoliosCount || b.issues - a.issues
    )
    const byHours = [...perUserStats].sort(
      (a, b) => b.hoursCount - a.hoursCount || b.issues - a.issues
    )

    return {
      peakHour,
      lowestHour,
      fullCoverageHours,
      overallCoverage,
      performanceScore,
      portfoliosChecked: uniquePortfoliosToday.size,
      totalPortfolios,
      totalIssuesLogged: filteredIssues.length,
      activeHours,
      perUserStats,
      leaderboard: {
        issuesYes: byIssuesYes.slice(0, 7),
        coverage: byCoverage.slice(0, 7),
        hours: byHours.slice(0, 7),
      },
    }
  }, [issues, portfolios, hourlyCoverage, selectedRange, customStartDate, customEndDate])

  const getPerformanceLabel = (score: number) => {
    if (score >= 8) return { text: 'Excellent', color: 'text-green-600', bgColor: 'bg-green-50', borderColor: 'border-green-200' }
    if (score >= 5) return { text: 'Good', color: 'text-lime-600', bgColor: 'bg-lime-50', borderColor: 'border-lime-200' }
    if (score >= 3) return { text: 'Fair', color: 'text-yellow-600', bgColor: 'bg-yellow-50', borderColor: 'border-yellow-200' }
    return { text: 'Needs Improvement', color: 'text-red-600', bgColor: 'bg-red-50', borderColor: 'border-red-200' }
  }

  const performanceLabel = getPerformanceLabel(analytics.performanceScore)

  const handleExportCSV = () => {
    const csvRows = [
      ['User', 'Total Count', 'Total Portfolios Monitored', 'Monitoring Active Hours', 'Missed Alerts'],
      ...analytics.perUserStats.map((u) => [
        u.displayName,
        u.issues.toString(),
        u.portfoliosCount.toString(),
        u.hoursCount.toString(),
        u.missedAlerts.toString(),
      ]),
    ]

    const csvContent = csvRows.map((row) => row.join(',')).join('\n')
    const blob = new Blob([csvContent], { type: 'text/csv' })
    const url = window.URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `performance_analytics_${new Date().toISOString().split('T')[0]}.csv`
    a.click()
    window.URL.revokeObjectURL(url)
    toast.success('Performance analytics exported successfully')
  }

  // Calculate consistent hours with 70%+ coverage
  const consistentHours = hourlyCoverage.filter((h) => h.coverage >= 70).length

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Performance Analytics</h1>
          <p className="text-gray-600 mt-1">Coverage, activity, and portfolio insights</p>
        </div>
      </div>

      {/* Date Range Filters */}
      <div className="flex items-center gap-4">
        <span className="text-sm font-medium text-gray-700">Range:</span>
        <div className="flex items-center gap-2">
          <button
            onClick={() => setSelectedRange('today')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedRange === 'today'
                ? 'text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
            style={selectedRange === 'today' ? { backgroundColor: '#76ab3f' } : {}}
          >
            Today
          </button>
          <button
            onClick={() => setSelectedRange('yesterday')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedRange === 'yesterday'
                ? 'text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
            style={selectedRange === 'yesterday' ? { backgroundColor: '#76ab3f' } : {}}
          >
            Yesterday
          </button>
          <button
            onClick={() => setSelectedRange('week')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedRange === 'week'
                ? 'text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
            style={selectedRange === 'week' ? { backgroundColor: '#76ab3f' } : {}}
          >
            Last 7 days
          </button>
          <button
            onClick={() => setSelectedRange('custom')}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
              selectedRange === 'custom'
                ? 'text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
            style={selectedRange === 'custom' ? { backgroundColor: '#76ab3f' } : {}}
          >
            Custom Range
          </button>
        </div>
        <span className="text-sm text-gray-600">
          Showing {analytics.totalIssuesLogged} issues
        </span>
        {selectedRange === 'custom' && (
          <div className="flex items-center gap-4">
            <input
              type="date"
              value={customStartDate}
              onChange={(e) => setCustomStartDate(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            />
            <input
              type="date"
              value={customEndDate}
              onChange={(e) => setCustomEndDate(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            />
          </div>
        )}
      </div>

      {/* Main Metrics - Top Row - All Same Size */}
      <div className="grid grid-cols-4 gap-4">
        <MetricCard
          label="Overall Coverage"
          value={`${Math.round(analytics.overallCoverage)}%`}
          accent="text-green-600"
          highlight="bg-green-100"
        />
        <MetricCard
          label="Portfolios Checked"
          value={analytics.portfoliosChecked}
          highlight="bg-blue-100"
        />
        <MetricCard
          label="Monitoring Active Hours"
          value={`${analytics.activeHours}/24`}
          highlight="bg-orange-100"
        />
        <MetricCard
          label="Issues Logged"
          value={analytics.totalIssuesLogged}
          highlight="bg-purple-100"
        />
      </div>

      {/* Second Row - 24h Coverage Score + 3 Detail Panels - All Same Height */}
      <div className="grid grid-cols-4 gap-4">
        {/* 24h Coverage Score */}
        <Card className="flex flex-col h-full">
          <div className={`text-center p-5 rounded-lg border-2 flex-1 flex flex-col justify-center ${performanceLabel.bgColor} ${performanceLabel.borderColor}`}>
            <div className={`text-3xl font-bold ${performanceLabel.color} mb-2`}>
              {analytics.performanceScore}/10
            </div>
            <div className={`text-lg font-semibold ${performanceLabel.color} mb-1`}>
              {performanceLabel.text.toUpperCase()}
            </div>
            <div className="text-xs text-gray-600">24h coverage score</div>
          </div>
        </Card>

        {/* Coverage Window */}
        <Card className="flex flex-col h-full">
          <h3 className="text-sm font-semibold text-gray-700 mb-2">Coverage Window</h3>
          <p className="text-xs text-gray-600 mb-3">Hourly distribution of coverage.</p>
          <div className="space-y-2 flex-1">
            <div>
              <span className="text-xs text-gray-600">Peak hour: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.peakHour.hour}:00</span>
            </div>
            <div>
              <span className="text-xs text-gray-600">Lowest hour: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.lowestHour.hour}:00</span>
            </div>
            <div>
              <span className="text-xs text-gray-600">Hours with full coverage: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.fullCoverageHours}/24</span>
            </div>
          </div>
        </Card>

        {/* Activity Snapshot */}
        <Card className="flex flex-col h-full">
          <h3 className="text-sm font-semibold text-gray-700 mb-2">Activity Snapshot</h3>
          <p className="text-xs text-gray-600 mb-3">What's been logged so far.</p>
          <div className="space-y-2 flex-1">
            <div>
              <span className="text-xs text-gray-600">Total Count: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.totalIssuesLogged}</span>
            </div>
            <div>
              <span className="text-xs text-gray-600">Monitoring Active Hours: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.activeHours}</span>
            </div>
            <div>
              <span className="text-xs text-gray-600">Consistent hours (70%+): </span>
              <span className="text-sm font-semibold text-gray-900">{consistentHours}</span>
            </div>
            <p className="text-xs text-gray-500 mt-2 italic">
              Consistency measures how many hours met the 70% coverage benchmark.
            </p>
          </div>
        </Card>

        {/* Portfolio Coverage */}
        <Card className="flex flex-col h-full">
          <h3 className="text-sm font-semibold text-gray-700 mb-2">Portfolio Coverage</h3>
          <p className="text-xs text-gray-600 mb-3">How many portfolios have been touched.</p>
          <div className="space-y-2 flex-1">
            <div>
              <span className="text-xs text-gray-600">Checked: </span>
              <span className="text-sm font-semibold text-gray-900">{analytics.portfoliosChecked}</span>
              <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                <div
                  className="bg-green-600 h-2 rounded-full"
                  style={{ width: `${Math.min((analytics.portfoliosChecked / analytics.totalPortfolios) * 100, 100)}%` }}
                ></div>
              </div>
            </div>
            <div>
              <span className="text-xs text-gray-600">Need attention: </span>
              <span className="text-sm font-semibold text-gray-900">{Math.max(0, analytics.totalPortfolios - analytics.portfoliosChecked)}</span>
            </div>
          </div>
        </Card>
      </div>

      {/* Bottom Section - Top Performers + Team Performance */}
      <div className="grid grid-cols-12 gap-6">
        {/* Top Performers - Left Side */}
        <div className="col-span-4">
          <Card>
            <h2 className="text-lg font-semibold text-gray-900 mb-2">Top Performers</h2>
            <p className="text-sm text-gray-600 mb-4">Leaders by coverage and findings.</p>
            
            <div className="space-y-4">
              <div>
                <h3 className="text-sm font-semibold text-gray-700 mb-2">Most Total Portfolios Monitored</h3>
                <div className="space-y-2">
                  {analytics.leaderboard.coverage.slice(0, 5).map((user, idx) => (
                    <div
                      key={user.user}
                      className="flex items-center justify-between p-2 rounded hover:bg-gray-50 transition-colors"
                    >
                      <div className="flex items-center gap-2">
                        <span className="text-sm font-semibold text-gray-900">{idx + 1}</span>
                        <span className="text-sm text-gray-900">{user.displayName || user.user}</span>
                      </div>
                      <span className="text-sm font-bold text-gray-900">{user.portfoliosCount}</span>
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <h3 className="text-sm font-semibold text-gray-700 mb-2">Most Active Issues</h3>
                <div className="space-y-2">
                  {analytics.leaderboard.issuesYes.slice(0, 5).map((user, idx) => (
                    <div
                      key={user.user}
                      className="flex items-center justify-between p-2 rounded hover:bg-gray-50 transition-colors"
                    >
                      <div className="flex items-center gap-2">
                        <span className="text-sm font-semibold text-gray-900">{idx + 1}</span>
                        <span className="text-sm text-gray-900">{user.displayName || user.user}</span>
                      </div>
                      <span className="text-sm font-bold text-gray-900">{user.issuesYes}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </Card>
        </div>

        {/* Team Performance Table - Right Side */}
        <div className="col-span-8">
          <Card>
            <div className="flex items-center justify-between mb-4">
              <div>
                <h2 className="text-lg font-semibold text-gray-900">Team Performance</h2>
                <p className="text-sm text-gray-600">Filtered by: {
                  selectedRange === 'today' ? 'Today' :
                  selectedRange === 'yesterday' ? 'Yesterday' :
                  selectedRange === 'week' ? 'Last 7 days' :
                  'Custom Range'
                }</p>
              </div>
              <Button onClick={handleExportCSV} variant="primary" size="sm">
                Export Data
              </Button>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-4 py-3 text-left text-xs font-semibold text-gray-700 uppercase border-b border-gray-200">
                      User
                    </th>
                    <th className="px-4 py-3 text-right text-xs font-semibold text-gray-700 uppercase border-b border-gray-200">
                      Total Count
                    </th>
                    <th className="px-4 py-3 text-right text-xs font-semibold text-gray-700 uppercase border-b border-gray-200">
                      Total Portfolios Monitored
                    </th>
                    <th className="px-4 py-3 text-right text-xs font-semibold text-gray-700 uppercase border-b border-gray-200">
                      Monitoring Active Hours
                    </th>
                    <th className="px-4 py-3 text-right text-xs font-semibold text-gray-700 uppercase border-b border-gray-200">
                      Missed Alerts
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {analytics.perUserStats.map((u, idx) => (
                    <tr
                      key={u.user}
                      className="hover:bg-gray-50 transition-colors"
                    >
                      <td className="px-4 py-3 whitespace-nowrap">
                        <div className="flex items-center">
                          <div
                            className="flex-shrink-0 h-8 w-8 rounded-full flex items-center justify-center text-white font-bold text-xs"
                            style={{ backgroundColor: '#76AB3F' }}
                          >
                            {(u.displayName || u.user || '?').charAt(0).toUpperCase()}
                          </div>
                          <div className="ml-3">
                            <div className="text-sm font-semibold text-gray-900">{u.displayName || u.user}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-4 py-3 whitespace-nowrap text-right">
                        <span className="text-sm font-semibold text-gray-900">{u.issues}</span>
                      </td>
                      <td className="px-4 py-3 whitespace-nowrap text-right">
                        <span className="text-sm font-semibold text-gray-900">{u.portfoliosCount}</span>
                      </td>
                      <td className="px-4 py-3 whitespace-nowrap text-right">
                        <span className="text-sm font-semibold text-gray-900">{u.hoursCount}</span>
                      </td>
                      <td className="px-4 py-3 whitespace-nowrap text-right">
                        <span
                          className={`inline-flex items-center px-2 py-1 rounded text-xs font-semibold ${
                            (u.missedAlerts || 0) === 0
                              ? 'text-white'
                              : (u.missedAlerts || 0) <= 3
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-red-100 text-red-800'
                          }`}
                          style={(u.missedAlerts || 0) === 0 ? { backgroundColor: '#76AB3F' } : {}}
                        >
                          {u.missedAlerts || 0}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </Card>
        </div>
      </div>
    </div>
  )
}

interface MetricCardProps {
  label: string
  value: string | number
  accent?: string
  highlight?: string
}

const MetricCard: React.FC<MetricCardProps> = ({ label, value, accent, highlight }) => (
  <Card className="h-full flex flex-col">
    <div className={`${highlight || 'bg-white'} rounded-lg p-5 flex-1 flex flex-col justify-center`}>
      <p className="text-sm font-medium text-gray-700 mb-2">{label}</p>
      <p className={`text-3xl font-bold ${accent || 'text-gray-900'}`}>{value}</p>
    </div>
  </Card>
)

interface LeaderboardCardProps {
  title: string
  data: Array<{
    user: string
    displayName: string
    issues: number
    issuesYes: number
    portfoliosCount: number
    hoursCount: number
    missedAlerts: number
  }>
  getValue: (user: any) => number
}

const LeaderboardCard: React.FC<LeaderboardCardProps> = ({ title, data, getValue }) => (
  <Card>
    <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
    <div className="space-y-2">
      {data.length === 0 ? (
        <p className="text-sm text-gray-500">No data available</p>
      ) : (
        data.map((user, idx) => (
          <div
            key={user.user}
            className="flex items-center justify-between p-3 rounded-lg hover:bg-gray-50 transition-colors cursor-pointer"
          >
            <div className="flex items-center gap-3">
              <div
                className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-bold text-xs ${
                  idx < 3 ? 'bg-gradient-to-br from-green-600 to-green-800' : 'bg-gray-400'
                }`}
              >
                {idx + 1}
              </div>
              <div>
                <p className="text-sm font-semibold text-gray-900">{user.displayName || user.user}</p>
              </div>
            </div>
            <span className="text-sm font-bold text-gray-900">{getValue(user)}</span>
          </div>
        ))
      )}
    </div>
  </Card>
)

export default PerformanceAnalytics
