import React, { useState } from 'react'
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom'
import { useAuth } from '../../context/AuthContext'
import { useTenant } from '../../context/TenantContext'
import toast from 'react-hot-toast'

const MainLayout: React.FC = () => {
  const { user, logout } = useAuth()
  const { selectedTenantId, selectedTenant, setSelectedTenantId, tenants, isLoading: tenantsLoading } = useTenant()
  const location = useLocation()
  const navigate = useNavigate()
  const [sidebarOpen, setSidebarOpen] = useState(true)
  const isSuperAdmin = user?.role === 'super_admin'

  const handleLogout = async () => {
    try {
      await logout()
      navigate('/login')
      toast.success('Logged out successfully')
    } catch (error) {
      toast.error('Failed to logout')
    }
  }

  const getCurrentHourEST = () => {
    const now = new Date()
    const estOffset = -5 // EST is UTC-5
    const utc = now.getTime() + (now.getTimezoneOffset() * 60000)
    const estTime = new Date(utc + (estOffset * 3600000))
    return estTime.getHours()
  }

  const navItems = [
    { path: '/', label: 'Dashboard & Log Issues', icon: 'ðŸ“Š' },
    { path: '/issues', label: 'Issue Details', icon: 'ðŸŽ«' },
    { path: '/analytics', label: 'Performance Analytics', icon: 'ðŸ“ˆ' },
    { path: '/issues-by-user', label: 'Issues by User', icon: 'ðŸ‘¥' },
    { path: '/coverage-matrix', label: 'Coverage Matrix', icon: 'ðŸ“Š' },
  ]

  // Admin Panel is shown as a button in the header, not in the navigation bar

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Top Header */}
      <header className="bg-white border-b-2 shadow-sm" style={{ borderColor: '#76ab3f' }}>
        <div className="px-6 py-3">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <div className="flex items-center flex-shrink-0">
              <img 
                src="/navbar.png" 
                alt="American Green Solutions" 
                className="h-12 w-auto"
              />
            </div>

            {/* Center Title */}
            <div className="flex-1 text-center px-4">
              <h1 className="text-3xl font-bold text-gray-900" style={{ fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif', letterSpacing: '-0.02em' }}>
                {isSuperAdmin ? (selectedTenant?.name || 'Select Client') : (user?.tenantName || 'Standard Solar')}
              </h1>
              <p className="text-lg text-gray-700 font-semibold mt-0.5" style={{ fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>Issue Tracker</p>
            </div>

            {/* Right Side */}
            <div className="flex items-center gap-3 flex-shrink-0">
              {/* Client Selector for Super Admin */}
              {isSuperAdmin && (
                <div className="flex items-center gap-2">
                  <label className="text-sm font-medium text-gray-700 whitespace-nowrap">Client:</label>
                  <select
                    value={selectedTenantId || ''}
                    onChange={(e) => {
                      const tenantId = e.target.value || null
                      setSelectedTenantId(tenantId)
                      // Refresh the page to reload data with new tenant
                      window.location.reload()
                    }}
                    className="px-3 py-1.5 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-sm font-medium text-gray-900 min-w-[180px]"
                    disabled={tenantsLoading}
                  >
                    {tenantsLoading ? (
                      <option>Loading...</option>
                    ) : tenants.length === 0 ? (
                      <option>No clients available</option>
                    ) : (
                      <>
                        <option value="">Select a client</option>
                        {tenants.map((tenant) => (
                          <option key={tenant.tenant_id} value={tenant.tenant_id}>
                            {tenant.name}
                          </option>
                        ))}
                      </>
                    )}
                  </select>
                </div>
              )}
              <div className="flex items-center gap-2.5">
                <div className="w-10 h-10 rounded-full flex items-center justify-center border-2 flex-shrink-0" style={{ backgroundColor: '#f0f9f0', borderColor: '#76ab3f' }}>
                  <span className="font-bold text-base" style={{ color: '#76ab3f' }}>
                    {(user?.fullName || user?.email || 'A')[0].toUpperCase()}
                  </span>
                </div>
                <div className="text-left min-w-0">
                  <p className="text-sm font-semibold text-gray-900 leading-tight truncate">{user?.fullName || 'Admin'}</p>
                  <p className="text-xs text-gray-600 capitalize leading-tight">{user?.role?.replace('_', ' ') || 'administrator'}</p>
                </div>
              </div>
              {(user?.role === 'tenant_admin' || user?.role === 'super_admin') && (
                <Link
                  to="/admin"
                  className="px-4 py-2 text-white rounded-lg transition-colors font-medium shadow-sm hover:opacity-90 whitespace-nowrap"
                  style={{ backgroundColor: '#76ab3f', fontSize: '14px' }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#5f8a32'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#76ab3f'}
                >
                  Admin Panel
                </Link>
              )}
              <button
                onClick={handleLogout}
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors font-medium shadow-sm whitespace-nowrap"
                style={{ fontSize: '14px' }}
              >
                Logout
              </button>
              <div className="px-3 py-2 text-white rounded-lg font-semibold whitespace-nowrap" style={{ backgroundColor: '#76ab3f', fontSize: '13px' }}>
                Current Hour (EST) {getCurrentHourEST()}
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Bar */}
        <nav className="px-6" style={{ backgroundColor: '#76ab3f' }}>
          <div className="flex items-center gap-1">
            {navItems.map((item) => {
              const isActive = location.pathname === item.path || 
                (item.path === '/' && location.pathname === '/')
              return (
                <Link
                  key={item.path}
                  to={item.path}
                  className="px-6 py-3 text-white font-medium transition-colors relative hover:opacity-80"
                  style={{ 
                    backgroundColor: isActive ? '#5f8a32' : 'transparent'
                  }}
                >
                  {item.label}
                  {isActive && (
                    <div className="absolute bottom-0 left-0 right-0 h-1 bg-white"></div>
                  )}
                </Link>
              )
            })}
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="px-6 py-6">
        <Outlet />
      </main>
    </div>
  )
}

export default MainLayout
