import React, { useState, useMemo } from 'react'
import { useQuery } from '@tanstack/react-query'
import { Bar } from 'react-chartjs-2'
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js'
import { issueService } from '../../services/issueService'
import { portfolioService } from '../../services/portfolioService'
import { adminService } from '../../services/adminService'
import { Issue } from '../../types/issue.types'
import { Portfolio } from '../../types/portfolio.types'
import { User } from '../../types/user.types'
import { useAuth } from '../../context/AuthContext'
import Card from '../common/Card'
import Button from '../common/Button'
import Modal from '../common/Modal'
import toast from 'react-hot-toast'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend)

interface UserHourData {
  portfolios: Set<string>
  issues: Issue[]
  activeIssues: Issue[]
}

interface UserMatrixData {
  userName: string
  displayName: string
  email: string
  totalPortfolios: number
  hours: { [hour: number]: UserHourData }
}

const CoverageMatrix: React.FC = () => {
  const { user: currentUser } = useAuth()
  const [userSearch, setUserSearch] = useState('')
  const [hourFilter, setHourFilter] = useState<string>('all')
  const [issueFilter, setIssueFilter] = useState<'all' | 'active'>('all')
  const [fromDate, setFromDate] = useState(() => {
    // Default to today
    const today = new Date()
    return today.toISOString().split('T')[0]
  })
  const [toDate, setToDate] = useState(() => {
    const today = new Date()
    return today.toISOString().split('T')[0]
  })
  const [showCharts, setShowCharts] = useState(true)
  const [selectedUser, setSelectedUser] = useState<UserMatrixData | null>(null)
  const [hoveredCell, setHoveredCell] = useState<{
    userId: string
    hour: number
    position: { top: number; left: number }
  } | null>(null)

  // Fetch portfolios
  const { data: portfolios = [] } = useQuery<Portfolio[]>({
    queryKey: ['portfolios'],
    queryFn: portfolioService.getAll,
  })

  // Fetch users to map user IDs to emails
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['admin-users'],
    queryFn: adminService.getUsers,
  })

  // Create mapping from user ID to user info (email, full_name)
  const userIdToUser = useMemo(() => {
    const map: { [key: string]: { email: string; full_name: string } } = {}
    users.forEach((user) => {
      map[user.id] = {
        email: user.email,
        full_name: user.full_name || user.email.split('@')[0]
      }
    })
    return map
  }, [users])

  // Fetch issues
  const { data: allIssues = [] } = useQuery<Issue[]>({
    queryKey: ['issues'],
    queryFn: () => issueService.getAll({}),
  })

  // Filter issues by date range
  const filteredIssues = useMemo(() => {
    let filtered = allIssues

    if (fromDate && toDate) {
      // Use date string comparison to avoid timezone issues
      filtered = filtered.filter((issue) => {
        if (!issue.created_at) return false
        const issueDateStr = new Date(issue.created_at).toISOString().split('T')[0]
        return issueDateStr >= fromDate && issueDateStr <= toDate
      })
    }

    // Issue filter
    if (issueFilter === 'active') {
      filtered = filtered.filter(
        (issue) => issue.description && issue.description.toLowerCase() !== 'no issue'
      )
    }

    // Hour filter
    if (hourFilter !== 'all') {
      const hour = parseInt(hourFilter)
      filtered = filtered.filter((issue) => issue.issue_hour === hour)
    }

    return filtered
  }, [allIssues, fromDate, toDate, issueFilter, hourFilter])

  // Build matrix data - based on "All Sites Checked = Yes" completions
  // Count portfolios based on each time a portfolio is completed (not just issues entered)
  const matrixData = useMemo(() => {
    const userMap: { [key: string]: UserMatrixData } = {}

    // Normalize date range for comparison
    const normalizedFromDate = fromDate ? fromDate.split('T')[0] : null
    const normalizedToDate = toDate ? toDate.split('T')[0] : null

    // First, process portfolios with "All Sites Checked = Yes" to count completions
    // Count each distinct issue entry session (by hour) as a separate completion
    // This allows the same portfolio to be counted multiple times if completed multiple times
    console.log('Coverage Matrix Debug - Processing portfolios:', {
      totalPortfolios: portfolios.length,
      completedPortfolios: portfolios.filter(p => p.all_sites_checked === 'Yes').length,
      dateRange: { fromDate: normalizedFromDate, toDate: normalizedToDate },
      totalIssues: filteredIssues.length,
      completedPortfolioDetails: portfolios
        .filter(p => p.all_sites_checked === 'Yes')
        .map(p => ({
          id: p.id,
          name: p.name,
          checkedBy: p.all_sites_checked_by,
          checkedDate: p.all_sites_checked_date,
          checkedHour: p.all_sites_checked_hour,
        })),
    })

    // Debug: Log all completed portfolios first
    const completedPortfolios = portfolios.filter(p => p.all_sites_checked === 'Yes' && p.all_sites_checked_date && p.all_sites_checked_by)
    console.log('ðŸ” Coverage Matrix - All completed portfolios:', completedPortfolios.map(p => ({
      id: p.id,
      name: p.name,
      checkedBy: p.all_sites_checked_by,
      checkedDate: p.all_sites_checked_date,
      checkedHour: p.all_sites_checked_hour,
    })))
    console.log('ðŸ” Coverage Matrix - Available users:', users.map(u => ({ id: u.id, email: u.email, full_name: u.full_name })))
    console.log('ðŸ” Coverage Matrix - Date range:', { fromDate, toDate, normalizedFromDate, normalizedToDate })

    portfolios.forEach((portfolio) => {
      // Only count portfolios that have been completed (all_sites_checked = 'Yes')
      if (portfolio.all_sites_checked !== 'Yes' || !portfolio.all_sites_checked_date || !portfolio.all_sites_checked_by) {
        return
      }

      // Check if completion date is within the selected date range
      const checkedDateStr = portfolio.all_sites_checked_date
      const normalizedCheckedDate = checkedDateStr.split('T')[0] // Remove time if present

      // Strict date filtering - portfolio completion must be within the selected date range
      if (normalizedFromDate && normalizedCheckedDate < normalizedFromDate) {
        console.log('â­ï¸ Coverage Matrix - Skipping portfolio (before date range):', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          checkedDate: normalizedCheckedDate,
          fromDate: normalizedFromDate,
        })
        return // Skip - before date range
      }
      if (normalizedToDate && normalizedCheckedDate > normalizedToDate) {
        console.log('â­ï¸ Coverage Matrix - Skipping portfolio (after date range):', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          checkedDate: normalizedCheckedDate,
          toDate: normalizedToDate,
        })
        return // Skip - after date range
      }

      // Get the user who completed it (all_sites_checked_by might be a user ID/UUID or email)
      // Try ID match first
      let checkedByUser = users.find((u) => u.id === portfolio.all_sites_checked_by)

      // If ID match fails, try email match (fallback for data inconsistencies)
      if (!checkedByUser) {
        console.warn('âš ï¸ Coverage Matrix - User ID not found, trying email match:', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          checkedByValue: portfolio.all_sites_checked_by,
          checkedByType: typeof portfolio.all_sites_checked_by,
        })
        checkedByUser = users.find((u) => 
          u.email.toLowerCase() === String(portfolio.all_sites_checked_by).toLowerCase()
        )
      }

      if (!checkedByUser) {
        console.error('âŒ Coverage Matrix - User not found for portfolio completion (tried both ID and email):', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          checkedByValue: portfolio.all_sites_checked_by,
          checkedByType: typeof portfolio.all_sites_checked_by,
          checkedDate: normalizedCheckedDate,
          availableUsers: users.map(u => ({ 
            id: u.id, 
            idType: typeof u.id,
            email: u.email, 
            full_name: u.full_name,
            idMatches: u.id === portfolio.all_sites_checked_by,
            emailMatches: u.email.toLowerCase() === String(portfolio.all_sites_checked_by).toLowerCase(),
          })),
          availableUserIds: users.map(u => u.id),
          availableUserEmails: users.map(u => u.email),
          searchingFor: portfolio.all_sites_checked_by,
        })
        return
      }

      const userEmail = checkedByUser.email
      const displayName = checkedByUser.full_name || userEmail.split('@')[0] || 'Unknown'

      console.log('Coverage Matrix Debug - Processing completed portfolio:', {
        portfolioId: portfolio.id,
        portfolioName: portfolio.name,
        checkedBy: userEmail,
        checkedDate: normalizedCheckedDate,
        completionHour: portfolio.all_sites_checked_hour,
      })

      // Get all issues for this portfolio by this user within the date range
      const portfolioIssues = filteredIssues.filter(issue => {
        if (issue.portfolio_id !== portfolio.id) return false

        // Check if issue is by this user
        const issueMonitoredBy = Array.isArray(issue.monitored_by) 
          ? issue.monitored_by[0] 
          : issue.monitored_by
        if (!issueMonitoredBy || issueMonitoredBy.toLowerCase() !== userEmail.toLowerCase()) {
          return false
        }

        // Check if issue date is within range
        if (issue.created_at) {
          const issueDateStr = new Date(issue.created_at).toISOString().split('T')[0]
          if (normalizedFromDate && issueDateStr < normalizedFromDate) return false
          if (normalizedToDate && issueDateStr > normalizedToDate) return false
        }

        return true
      })

      // Get completion hour from portfolio record
      const completionHour = portfolio.all_sites_checked_hour ?? 0

      console.log('Coverage Matrix Debug - Portfolio issues found:', {
        portfolioId: portfolio.id,
        portfolioName: portfolio.name,
        userEmail,
        issuesFound: portfolioIssues.length,
        completionHour,
        issueHours: portfolioIssues.map(i => i.issue_hour),
        allIssuesForPortfolio: portfolioIssues.map(i => ({
          id: i.id,
          hour: i.issue_hour,
          monitoredBy: i.monitored_by,
          created_at: i.created_at,
        })),
      })

      // Initialize user in map if not exists
      if (!userMap[userEmail]) {
        userMap[userEmail] = {
          userName: userEmail,
          displayName,
          email: userEmail,
          totalPortfolios: 0,
          hours: {},
        }
      }

      // If there are issues, group by hour and count each hour separately
      // ALWAYS use issue hour when issues exist, not completion hour
      if (portfolioIssues.length > 0) {
        // Group issues by hour - each hour represents a distinct work session/completion
        const issuesByHour: { [hour: number]: typeof portfolioIssues } = {}
        portfolioIssues.forEach(issue => {
          const hour = issue.issue_hour ?? 0
          if (!issuesByHour[hour]) {
            issuesByHour[hour] = []
          }
          issuesByHour[hour].push(issue)
        })

        console.log('âœ… Coverage Matrix - Counting portfolio by issue hours:', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          userEmail,
          displayName,
          issuesByHourKeys: Object.keys(issuesByHour),
          completionHour, // This might be different from issue hours
          totalIssues: portfolioIssues.length,
        })

        // Count each hour with issues as a separate portfolio completion
        Object.keys(issuesByHour).forEach(hourStr => {
          const hour = parseInt(hourStr)

          // Check hour filter
          if (hourFilter !== 'all' && hour !== parseInt(hourFilter)) {
            console.log('â­ï¸ Coverage Matrix - Skipping hour (filtered out):', { hour, hourFilter })
            return
          }

          // Initialize hour if not exists
          if (!userMap[userEmail].hours[hour]) {
            userMap[userEmail].hours[hour] = {
              portfolios: new Set(),
              issues: [],
              activeIssues: [],
            }
          }

          // Add portfolio completion - use unique key: portfolioId_date_hour
          // This allows the same portfolio to be counted multiple times if completed at different hours
          // IMPORTANT: Use issue hour, not completion hour, because that's when the work was actually done
          const portfolioKey = `${portfolio.id}_${normalizedCheckedDate}_${hour}`
          const wasAdded = userMap[userEmail].hours[hour].portfolios.has(portfolioKey)
          userMap[userEmail].hours[hour].portfolios.add(portfolioKey)

          console.log('âœ… Coverage Matrix - Added portfolio to hour:', {
            portfolioId: portfolio.id,
            portfolioName: portfolio.name,
            userEmail,
            displayName,
            hour,
            portfolioKey,
            wasAlreadyAdded: wasAdded,
            totalPortfoliosInThisHour: userMap[userEmail].hours[hour].portfolios.size,
          })
        })
      } else {
        // No issues found, but portfolio is completed - count using completion hour
        // This is a fallback for portfolios completed without issues logged
        console.log('âš ï¸ Coverage Matrix - No issues found, using completion hour:', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          userEmail,
          displayName,
          completionHour,
          checkedDate: normalizedCheckedDate,
        })

        // Check hour filter
        if (hourFilter !== 'all' && completionHour !== parseInt(hourFilter)) {
          console.log('â­ï¸ Coverage Matrix - Skipping completion hour (filtered out):', { completionHour, hourFilter })
          return
        }

        // Initialize hour if not exists
        if (!userMap[userEmail].hours[completionHour]) {
          userMap[userEmail].hours[completionHour] = {
            portfolios: new Set(),
            issues: [],
            activeIssues: [],
          }
        }

        // Add portfolio completion - use unique key: portfolioId_date_hour
        const portfolioKey = `${portfolio.id}_${normalizedCheckedDate}_${completionHour}`
        const wasAdded = userMap[userEmail].hours[completionHour].portfolios.has(portfolioKey)
        userMap[userEmail].hours[completionHour].portfolios.add(portfolioKey)

        console.log('âœ… Coverage Matrix - Added portfolio (no issues, using completion hour):', {
          portfolioId: portfolio.id,
          portfolioName: portfolio.name,
          userEmail,
          displayName,
          completionHour,
          portfolioKey,
          wasAlreadyAdded: wasAdded,
          totalPortfoliosInThisHour: userMap[userEmail].hours[completionHour].portfolios.size,
        })
      }
    })

    // Now process issues to count issues (not portfolios) per user per hour
    filteredIssues.forEach((issue) => {
      // Get monitored_by field (can be string or array)
      let monitoredByEmails: string[] = []
      if (issue.monitored_by) {
        if (Array.isArray(issue.monitored_by)) {
          monitoredByEmails = issue.monitored_by
            .filter((email): email is string => typeof email === 'string')
            .filter((email) => email.trim() !== '')
        } else {
          // Handle case where monitored_by might be a single string (though type says array)
          const emailValue = String(issue.monitored_by).trim()
          if (emailValue !== '') {
            monitoredByEmails = [emailValue]
          }
        }
      }

      // Skip if no monitored_by field
      if (monitoredByEmails.length === 0) {
        return
      }

      // Check hour filter
      if (hourFilter !== 'all' && issue.issue_hour !== undefined) {
        const hour = parseInt(hourFilter)
        if (issue.issue_hour !== hour) {
          return
        }
      }

      const hour = issue.issue_hour ?? 0

      // Process each user in monitored_by
      monitoredByEmails.forEach((monitoredByEmail) => {
        // Find user by email (case-insensitive match)
        const user = users.find((u) => u.email.toLowerCase() === monitoredByEmail.toLowerCase())
        if (!user) {
          // If user not found by email, skip (user might have been deleted or email changed)
          console.warn(`âŒ Coverage Matrix - User not found for email: "${monitoredByEmail}". Available users:`, {
            searchingFor: monitoredByEmail,
            searchingForLower: monitoredByEmail.toLowerCase(),
            availableUsers: users.map(u => ({
              email: u.email,
              emailLower: u.email.toLowerCase(),
              full_name: u.full_name,
              matches: u.email.toLowerCase() === monitoredByEmail.toLowerCase(),
            })),
            issueDetails: {
              issueId: issue.id,
              portfolioId: issue.portfolio_id,
              monitoredBy: issue.monitored_by,
              hour: issue.issue_hour,
            },
          })
          return
        }

        const userEmail = user.email
        const displayName = user.full_name || userEmail.split('@')[0] || 'Unknown'

        // Initialize user in map if not exists
        if (!userMap[userEmail]) {
          userMap[userEmail] = {
            userName: userEmail,
            displayName,
            email: userEmail,
            totalPortfolios: 0,
            hours: {},
          }
        }

        // Initialize hour if not exists
        if (!userMap[userEmail].hours[hour]) {
          userMap[userEmail].hours[hour] = {
            portfolios: new Set(),
            issues: [],
            activeIssues: [],
          }
        }

        // Add issues to track issue count (includes both Yes and No issues)
        // DO NOT count portfolios here - portfolios are only counted from "All Sites Checked = Yes" completions above
        userMap[userEmail].hours[hour].issues.push(issue)
        if (issue.description && issue.description.toLowerCase() !== 'no issue') {
          userMap[userEmail].hours[hour].activeIssues.push(issue)
        }
      })
    })

    // Calculate total portfolios per user

    // Calculate total portfolios per user
    Object.values(userMap).forEach((user) => {
      const allPortfolios = new Set<string>()
      Object.values(user.hours).forEach((hourData) => {
        hourData.portfolios.forEach((pid) => allPortfolios.add(pid))
      })
      user.totalPortfolios = allPortfolios.size
    })

    // Filter by user search
    const filteredUsers = Object.values(userMap).filter((user) =>
      user.displayName.toLowerCase().includes(userSearch.toLowerCase()) ||
      user.userName.toLowerCase().includes(userSearch.toLowerCase())
    )

    // Detailed breakdown of what's in the userMap for debugging
    const userMapDetails = Object.entries(userMap).map(([email, userData]) => {
      const hourDetails: { [hour: string]: { portfolioKeys: string[], portfolioCount: number } } = {}
      Object.entries(userData.hours).forEach(([hour, hourData]) => {
        hourDetails[hour] = {
          portfolioKeys: Array.from(hourData.portfolios),
          portfolioCount: hourData.portfolios.size,
        }
      })
      return {
        email,
        displayName: userData.displayName,
        totalPortfolios: userData.totalPortfolios,
        hours: hourDetails,
      }
    })

    // Final summary log
    console.log('ðŸ“Š Coverage Matrix - FINAL RESULTS:', {
      totalUsers: filteredUsers.length,
